INSERT INTO `masterclasses` (`id`, `slug`, `title`, `subtitle`, `description`, `category_id`, `thumbnail_url`, `preview_video_url`, `difficulty_level`, `estimated_hours`, `total_lessons`, `min_plan_required`, `is_featured`, `is_published`, `instructor_name`, `instructor_bio`, `instructor_avatar`, `tags`, `learning_outcomes`, `prerequisites`, `technologies_covered`, `created_at`, `updated_at`) VALUES (1,'redis-mastery','Redis Mastery','From caching to real-time applications','Master Redis, the lightning-fast in-memory data store. Learn caching strategies, pub/sub messaging, data structures, persistence, clustering, and how to integrate Redis with PHP, Node.js, and Python applications.',1,NULL,NULL,'intermediate',8.0,2,'free',1,1,'Oliver Bob','Full-stack developer and infrastructure architect with 15+ years of experience building scalable systems.',NULL,'[\"redis\", \"caching\", \"nosql\", \"performance\", \"pub-sub\", \"real-time\"]','[\"Implement efficient caching strategies\", \"Build real-time applications with pub/sub\", \"Configure Redis for high availability\", \"Integrate Redis with PHP, Node.js, and Python\", \"Monitor and optimize Redis performance\"]','[\"Basic command line knowledge\", \"Familiarity with at least one programming language\"]','[\"Redis\", \"Redis Sentinel\", \"Redis Cluster\", \"PHP\", \"Node.js\", \"Python\"]','2025-12-17 13:23:13','2025-12-17 13:23:13');
INSERT INTO `masterclasses` (`id`, `slug`, `title`, `subtitle`, `description`, `category_id`, `thumbnail_url`, `preview_video_url`, `difficulty_level`, `estimated_hours`, `total_lessons`, `min_plan_required`, `is_featured`, `is_published`, `instructor_name`, `instructor_bio`, `instructor_avatar`, `tags`, `learning_outcomes`, `prerequisites`, `technologies_covered`, `created_at`, `updated_at`) VALUES (2,'lxc-lxd-containers','LXC/LXD Container Mastery','System containers for production workloads','Deep dive into LXC and LXD, the powerful system container technology. Learn to create, manage, and orchestrate lightweight virtual machines that share the host kernel. Perfect for development sandboxes, CI/CD, and production deployments.',2,NULL,NULL,'intermediate',10.0,2,'free',1,1,'Oliver Bob','Full-stack developer and infrastructure architect with 15+ years of experience building scalable systems.',NULL,'[\"lxc\", \"lxd\", \"containers\", \"linux\", \"virtualization\", \"devops\"]','[\"Create and manage LXC/LXD containers\", \"Configure networking and storage\", \"Build custom container images\", \"Set up container clustering\", \"Implement security best practices\"]','[\"Linux command line proficiency\", \"Basic networking knowledge\"]','[\"LXC\", \"LXD\", \"ZFS\", \"BTRFS\", \"Linux Networking\", \"Snap\"]','2025-12-17 13:23:13','2025-12-17 13:23:13');
INSERT INTO `masterclasses` (`id`, `slug`, `title`, `subtitle`, `description`, `category_id`, `thumbnail_url`, `preview_video_url`, `difficulty_level`, `estimated_hours`, `total_lessons`, `min_plan_required`, `is_featured`, `is_published`, `instructor_name`, `instructor_bio`, `instructor_avatar`, `tags`, `learning_outcomes`, `prerequisites`, `technologies_covered`, `created_at`, `updated_at`) VALUES (3,'docker-complete','Docker Complete Masterclass','Containerize everything','The comprehensive Docker masterclass covering everything from basics to advanced orchestration. Build, ship, and run applications anywhere with Docker containers, Compose, and Swarm.',2,NULL,NULL,'beginner',12.0,2,'free',1,1,'Oliver Bob','Full-stack developer and infrastructure architect with 15+ years of experience building scalable systems.',NULL,'[\"docker\", \"containers\", \"devops\", \"microservices\", \"compose\", \"swarm\"]','[\"Build and manage Docker images\", \"Create multi-container applications with Compose\", \"Implement container networking\", \"Deploy with Docker Swarm\", \"Optimize images for production\"]','[\"Basic command line knowledge\", \"Understanding of web applications\"]','[\"Docker\", \"Docker Compose\", \"Docker Swarm\", \"Dockerfile\", \"Registry\"]','2025-12-17 13:23:13','2025-12-17 13:23:13');
INSERT INTO `masterclasses` (`id`, `slug`, `title`, `subtitle`, `description`, `category_id`, `thumbnail_url`, `preview_video_url`, `difficulty_level`, `estimated_hours`, `total_lessons`, `min_plan_required`, `is_featured`, `is_published`, `instructor_name`, `instructor_bio`, `instructor_avatar`, `tags`, `learning_outcomes`, `prerequisites`, `technologies_covered`, `created_at`, `updated_at`) VALUES (4,'proxmox-virtualization','Proxmox VE Masterclass','Enterprise virtualization made accessible','Master Proxmox Virtual Environment, the open-source virtualization platform. Learn to deploy VMs and containers, configure clustering, manage storage, and build a complete virtualization infrastructure.',2,NULL,NULL,'advanced',10.0,1,'free',1,1,'Oliver Bob','Full-stack developer and infrastructure architect with 15+ years of experience building scalable systems.',NULL,'[\"proxmox\", \"virtualization\", \"kvm\", \"qemu\", \"homelab\", \"enterprise\"]','[\"Install and configure Proxmox VE\", \"Create and manage VMs and containers\", \"Set up high-availability clusters\", \"Configure storage backends (ZFS, Ceph)\", \"Implement backup and disaster recovery\"]','[\"Linux system administration\", \"Basic networking\", \"Understanding of virtualization concepts\"]','[\"Proxmox VE\", \"KVM\", \"QEMU\", \"LXC\", \"ZFS\", \"Ceph\", \"Corosync\"]','2025-12-17 13:23:13','2025-12-17 13:23:13');
INSERT INTO `masterclasses` (`id`, `slug`, `title`, `subtitle`, `description`, `category_id`, `thumbnail_url`, `preview_video_url`, `difficulty_level`, `estimated_hours`, `total_lessons`, `min_plan_required`, `is_featured`, `is_published`, `instructor_name`, `instructor_bio`, `instructor_avatar`, `tags`, `learning_outcomes`, `prerequisites`, `technologies_covered`, `created_at`, `updated_at`) VALUES (5,'virtualmin-hosting','Virtualmin Hosting Masterclass','Professional web hosting control panel','Build and manage a professional web hosting environment with Virtualmin. Learn to configure virtual servers, manage domains, set up email, databases, and provide a complete hosting experience.',4,NULL,NULL,'intermediate',8.0,1,'free',0,1,'Oliver Bob','Full-stack developer and infrastructure architect with 15+ years of experience building scalable systems.',NULL,'[\"virtualmin\", \"webmin\", \"hosting\", \"cpanel-alternative\", \"server-management\"]','[\"Install and configure Virtualmin\", \"Manage virtual servers and domains\", \"Set up email hosting with spam filtering\", \"Configure databases and backups\", \"Secure your hosting environment\"]','[\"Linux server administration basics\", \"DNS fundamentals\"]','[\"Virtualmin\", \"Webmin\", \"Apache/Nginx\", \"Postfix\", \"Dovecot\", \"MySQL/MariaDB\", \"BIND\"]','2025-12-17 13:23:13','2025-12-17 13:23:13');
INSERT INTO `masterclasses` (`id`, `slug`, `title`, `subtitle`, `description`, `category_id`, `thumbnail_url`, `preview_video_url`, `difficulty_level`, `estimated_hours`, `total_lessons`, `min_plan_required`, `is_featured`, `is_published`, `instructor_name`, `instructor_bio`, `instructor_avatar`, `tags`, `learning_outcomes`, `prerequisites`, `technologies_covered`, `created_at`, `updated_at`) VALUES (6,'ginto-ai-platform','Ginto AI Platform Masterclass','Build your own AI-powered development environment','The ultimate masterclass for mastering the Ginto AI platform. Learn how this project combines LXC containers, Redis caching, real-time streaming, MCP integration, and AI chat to create a complete agentic computing environment. By mastering Ginto, you will learn multiple technologies superpowered by AI.',3,NULL,NULL,'intermediate',15.0,6,'free',1,1,'Oliver Bob','Creator of Ginto AI, full-stack developer and infrastructure architect passionate about making AI accessible to everyone.',NULL,'[\"ginto\", \"ai\", \"llm\", \"mcp\", \"agents\", \"php\", \"containers\", \"redis\"]','[\"Deploy and configure the Ginto AI platform\", \"Understand the agentic computing paradigm\", \"Build AI-powered sandboxes with LXC\", \"Integrate multiple LLM providers\", \"Create custom MCP tools and agents\", \"Master real-time streaming with SSE\", \"Implement Redis for caching and sessions\"]','[\"Basic PHP knowledge\", \"Command line familiarity\", \"Understanding of web applications\"]','[\"PHP 8.x\", \"LXC/LXD\", \"Redis\", \"Caddy\", \"MySQL/MariaDB\", \"SSE\", \"MCP Protocol\", \"Groq API\", \"Cerebras API\", \"Alpine Linux\"]','2025-12-17 13:23:13','2025-12-17 13:23:13');
INSERT INTO `masterclasses` (`id`, `slug`, `title`, `subtitle`, `description`, `category_id`, `thumbnail_url`, `preview_video_url`, `difficulty_level`, `estimated_hours`, `total_lessons`, `min_plan_required`, `is_featured`, `is_published`, `instructor_name`, `instructor_bio`, `instructor_avatar`, `tags`, `learning_outcomes`, `prerequisites`, `technologies_covered`, `created_at`, `updated_at`) VALUES (7,'mvc-backend-development','MVC & Backend Development','Master the MVC pattern and modern backend architecture - natively supported in Ginto AI','Learn the Model-View-Controller architectural pattern and modern backend development practices. This masterclass covers PHP, routing, database integration, and how Ginto AI natively supports these technologies for rapid application development.',4,NULL,NULL,'intermediate',12.0,0,'free',1,1,'Ginto Team',NULL,NULL,NULL,'[\"Understand MVC architecture deeply\", \"Build RESTful APIs from scratch\", \"Implement database patterns with Medoo ORM\", \"Create secure authentication systems\", \"Use Ginto AI to accelerate backend development\"]','[\"Basic PHP knowledge\", \"Understanding of HTTP\", \"Familiarity with databases\"]','[\"PHP 8.x\", \"MVC Pattern\", \"Medoo ORM\", \"MySQL\", \"REST APIs\", \"Ginto AI\"]','2025-12-17 13:48:19','2025-12-17 13:48:19');
