INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (1,6,'introduction','Introduction to Ginto AI','Understand what Ginto AI is and the vision behind the agentic computing paradigm.','text','<div class=\"prose dark:prose-invert max-w-none\">\n<h2>Welcome to Ginto AI</h2>\n<p>Ginto AI represents more than a development environment; it is your gateway to the <strong>agentic computing paradigm</strong>‚Äîwhere AI agents operate as first-class obedient virtual citizens interacting with the real world alongside traditional software.</p>\n\n<h3>What You Will Learn</h3>\n<ul>\n<li>The vision behind Ginto AI and agentic computing</li>\n<li>How multiple technologies work together in harmony</li>\n<li>Practical skills in containers, caching, streaming, and AI</li>\n<li>How to deploy your own AI-powered development environment</li>\n</ul>\n\n<h3>Technologies Covered</h3>\n<div class=\"grid grid-cols-2 md:grid-cols-3 gap-4 my-6\">\n<div class=\"bg-teal-50 dark:bg-teal-900/20 p-4 rounded-lg text-center\">\n<i class=\"fas fa-cube text-2xl text-teal-600 dark:text-teal-400 mb-2\"></i>\n<div class=\"font-medium\">LXC/LXD</div>\n</div>\n<div class=\"bg-teal-50 dark:bg-teal-900/20 p-4 rounded-lg text-center\">\n<i class=\"fas fa-database text-2xl text-teal-600 dark:text-teal-400 mb-2\"></i>\n<div class=\"font-medium\">Redis</div>\n</div>\n<div class=\"bg-violet-50 dark:bg-violet-900/20 p-4 rounded-lg text-center\">\n<i class=\"fas fa-robot text-2xl text-violet-600 dark:text-violet-400 mb-2\"></i>\n<div class=\"font-medium\">AI/LLM</div>\n</div>\n<div class=\"bg-blue-50 dark:bg-blue-900/20 p-4 rounded-lg text-center\">\n<i class=\"fab fa-php text-2xl text-blue-600 dark:text-blue-400 mb-2\"></i>\n<div class=\"font-medium\">PHP 8.x</div>\n</div>\n<div class=\"bg-green-50 dark:bg-green-900/20 p-4 rounded-lg text-center\">\n<i class=\"fas fa-server text-2xl text-green-600 dark:text-green-400 mb-2\"></i>\n<div class=\"font-medium\">Caddy</div>\n</div>\n<div class=\"bg-amber-50 dark:bg-amber-900/20 p-4 rounded-lg text-center\">\n<i class=\"fas fa-stream text-2xl text-violet-600 dark:text-violet-400 mb-2\"></i>\n<div class=\"font-medium\">SSE</div>\n</div>\n</div>\n</div>',NULL,NULL,1,10,1,1,NULL,NULL,'2025-12-17 13:23:13','2025-12-17 17:33:37');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (2,6,'architecture-overview','Architecture Overview','Explore the complete architecture of Ginto AI and how components interact.','text','<div class=\"prose dark:prose-invert max-w-none\">\n<h2>Ginto AI Architecture</h2>\n<p>Ginto AI is built on a modular architecture that combines several powerful technologies.</p>\n\n<h3>Core Components</h3>\n<ol>\n<li><strong>Web Layer</strong> - Caddy server with PHP-FPM handling HTTP requests</li>\n<li><strong>AI Layer</strong> - Multi-provider LLM integration (Groq, Cerebras, OpenAI)</li>\n<li><strong>Sandbox Layer</strong> - LXC containers providing isolated development environments</li>\n<li><strong>Cache Layer</strong> - Redis for sessions, caching, and real-time pub/sub</li>\n<li><strong>Proxy Layer</strong> - Node.js sandbox-proxy routing requests to containers</li>\n</ol>\n\n<h3>Request Flow</h3>\n<pre class=\"bg-gray-900 text-green-400 p-4 rounded-lg overflow-x-auto\"><code>User Request ‚Üí Caddy ‚Üí PHP Router ‚Üí Controller\n                                        ‚Üì\n                              LLM Provider (Groq/Cerebras)\n                                        ‚Üì\n                              Tool Execution (MCP)\n                                        ‚Üì\n                              Sandbox (LXC Container)\n                                        ‚Üì\n                              SSE Stream ‚Üí User</code></pre>\n</div>',NULL,NULL,2,15,1,1,NULL,NULL,'2025-12-17 13:23:13','2025-12-17 13:23:13');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (3,6,'setting-up-environment','Setting Up Your Environment','Install all prerequisites and prepare your system for Ginto AI.','text','<div class=\"prose dark:prose-invert max-w-none\">\n<h2>Environment Setup</h2>\n\n<h3>System Requirements</h3>\n<ul>\n<li>Ubuntu 22.04+ or Debian 12+</li>\n<li>4GB RAM minimum (8GB recommended)</li>\n<li>20GB disk space</li>\n<li>Root or sudo access</li>\n</ul>\n\n<h3>Install Prerequisites</h3>\n<pre class=\"bg-gray-900 text-green-400 p-4 rounded-lg overflow-x-auto\"><code># Update system\nsudo apt update && sudo apt upgrade -y\n\n# Install PHP 8.x with extensions\nsudo apt install php8.3-fpm php8.3-cli php8.3-mysql php8.3-curl \\\n    php8.3-mbstring php8.3-xml php8.3-zip php8.3-redis -y\n\n# Install Redis\nsudo apt install redis-server -y\nsudo systemctl enable redis-server\n\n# Install LXD (via snap)\nsudo snap install lxd\nsudo lxd init --auto\n\n# Install Node.js for sandbox-proxy\ncurl -fsSL https://deb.nodesource.com/setup_20.x | sudo -E bash -\nsudo apt install nodejs -y\n\n# Install Caddy\nsudo apt install -y debian-keyring debian-archive-keyring apt-transport-https\ncurl -1sLf https://dl.cloudsmith.io/public/caddy/stable/gpg.key | sudo gpg --dearmor -o /usr/share/keyrings/caddy-stable-archive-keyring.gpg\necho \"deb [signed-by=/usr/share/keyrings/caddy-stable-archive-keyring.gpg] https://dl.cloudsmith.io/public/caddy/stable/deb/debian any-version main\" | sudo tee /etc/apt/sources.list.d/caddy-stable.list\nsudo apt update && sudo apt install caddy -y</code></pre>\n</div>',NULL,NULL,3,20,0,1,NULL,NULL,'2025-12-17 13:23:13','2025-12-17 13:23:13');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (4,6,'lxc-sandbox-creation','Creating LXC Sandboxes','Learn how Ginto creates isolated development sandboxes using LXC containers.','text','<div class=\"prose dark:prose-invert max-w-none\">\n<h2>LXC Sandbox Creation</h2>\n\n<p>Ginto uses LXC containers to provide isolated, secure development environments for each user.</p>\n\n<h3>The ginto.sh Bootstrap Script</h3>\n<p>The <code>ginto.sh</code> script handles all container management:</p>\n\n<pre class=\"bg-gray-900 text-green-400 p-4 rounded-lg overflow-x-auto\"><code># Initialize LXD and create base image\n./bin/ginto.sh init\n\n# Create a new sandbox for a user\n./bin/ginto.sh create sandbox-username\n\n# List all sandboxes\n./bin/ginto.sh list\n\n# Reset and rebuild\n./bin/ginto.sh reset</code></pre>\n\n<h3>Base Image Creation</h3>\n<p>The base image includes:</p>\n<ul>\n<li>Alpine Linux (lightweight ~5MB base)</li>\n<li>PHP-FPM configured on port 9000</li>\n<li>Caddy web server on port 80</li>\n<li>Composer for PHP dependencies</li>\n<li>Node.js for JavaScript development</li>\n</ul>\n</div>',NULL,NULL,4,20,0,1,NULL,NULL,'2025-12-17 13:23:13','2025-12-17 13:23:13');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (5,6,'redis-integration','Redis Integration','Configure Redis for sessions, caching, and real-time features.','text','<div class=\"prose dark:prose-invert max-w-none\">\n<h2>Redis in Ginto AI</h2>\n\n<p>Redis powers several critical features in Ginto:</p>\n\n<h3>1. Session Storage</h3>\n<pre class=\"bg-gray-900 text-green-400 p-4 rounded-lg overflow-x-auto\"><code>// PHP session handler via Redis\nini_set(\'session.save_handler\', \'redis\');\nini_set(\'session.save_path\', \'tcp://127.0.0.1:6379\');</code></pre>\n\n<h3>2. MCP Cache</h3>\n<pre class=\"bg-gray-900 text-green-400 p-4 rounded-lg overflow-x-auto\"><code>// Cache discovered MCP tools\n$redis->setex(\"mcp:tools:$serverId\", 3600, json_encode($tools));</code></pre>\n\n<h3>3. Rate Limiting</h3>\n<pre class=\"bg-gray-900 text-green-400 p-4 rounded-lg overflow-x-auto\"><code>// Sliding window rate limit\n$key = \"ratelimit:$userId:$minute\";\n$redis->incr($key);\n$redis->expire($key, 60);</code></pre>\n\n<h3>4. Sandbox Proxy Session</h3>\n<p>The sandbox-proxy uses Redis to maintain session state across container restarts.</p>\n</div>',NULL,NULL,5,15,0,1,NULL,NULL,'2025-12-17 13:23:13','2025-12-17 13:23:13');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (6,6,'ai-provider-integration','AI Provider Integration','Connect multiple LLM providers and implement streaming responses.','text','<div class=\"prose dark:prose-invert max-w-none\">\n<h2>Multi-Provider LLM Integration</h2>\n\n<p>Ginto supports multiple AI providers with automatic failover:</p>\n\n<h3>Supported Providers</h3>\n<ul>\n<li><strong>Groq</strong> - GPT-OSS-120B with browser_search, vision models</li>\n<li><strong>Cerebras</strong> - Fast inference, GPT-OSS-120B</li>\n<li><strong>OpenAI</strong> - GPT-4o, GPT-4</li>\n</ul>\n\n<h3>Provider Selection Logic</h3>\n<pre class=\"bg-gray-900 text-green-400 p-4 rounded-lg overflow-x-auto\"><code>// Automatic provider selection based on query type\n$requiresGroq = $hasImage || $needsWebSearch;\n\nif ($requiresGroq) {\n    $keyData = $keyManager->getAvailableKey(\'groq\');\n} else {\n    $keyData = $keyManager->getFirstAvailableKey();\n}</code></pre>\n\n<h3>Streaming with SSE</h3>\n<pre class=\"bg-gray-900 text-green-400 p-4 rounded-lg overflow-x-auto\"><code>header(\'Content-Type: text/event-stream\');\n$provider->chatStream($messages, $tools, $options,\n    function($chunk, $toolCall) {\n        echo \"data: \" . json_encode([\'text\' => $chunk]) . \"\\n\\n\";\n        flush();\n    }\n);</code></pre>\n</div>',NULL,NULL,6,20,0,1,NULL,NULL,'2025-12-17 13:23:13','2025-12-17 13:23:13');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (7,1,'introduction','Introduction to Redis','What is Redis and why it matters for modern applications.','text','<div class=\"prose dark:prose-invert max-w-none\">\n<h2>What is Redis?</h2>\n\n<p>Redis (Remote Dictionary Server) is an open-source, in-memory data structure store. It can be used as a database, cache, message broker, and streaming engine.</p>\n\n<div class=\"info-box bg-teal-50 dark:bg-teal-900/20 border-l-4 border-teal-500 p-4 my-6 rounded-r\">\n<p class=\"font-semibold text-teal-700 dark:text-teal-300\">üîó Official Resources</p>\n<p class=\"text-teal-600 dark:text-teal-400\">This lesson covers Redis fundamentals. For official documentation, visit <a href=\"https://redis.io/docs/\" target=\"_blank\" class=\"underline\">redis.io/docs</a></p>\n</div>\n\n<h3>Why Redis?</h3>\n\n<ul>\n<li><strong>Speed</strong>: Redis stores data in memory, enabling sub-millisecond response times for most operations</li>\n<li><strong>Data Structures</strong>: Native support for strings, hashes, lists, sets, sorted sets, streams, and more</li>\n<li><strong>Persistence Options</strong>: RDB snapshots and AOF logging for data durability</li>\n<li><strong>Replication</strong>: Built-in primary-replica replication for high availability</li>\n<li><strong>Clustering</strong>: Horizontal scaling across multiple nodes</li>\n</ul>\n\n<h3>How Ginto Uses Redis</h3>\n\n<!-- Redis Architecture Image -->\n<div class=\"my-8\">\n<img src=\"/assets/images/lessons/ginto_redis.png\" alt=\"Ginto Redis Architecture\" class=\"rounded-xl border border-gray-700 shadow-lg w-full\" />\n<p class=\"text-center text-sm text-gray-500 dark:text-gray-400 mt-2\">Ginto\'s Redis-powered architecture for scalable user mapping</p>\n</div>\n\n<h4>‚úÖ What\'s Implemented Today: Scalable User Mapping</h4>\n\n<p>Every user in Ginto is internally mapped via Redis for scalability. Our Redis implementation is built to scale to millions of LXC/LXD containers that users share in the cloud.</p>\n\n<div class=\"bg-gray-100 dark:bg-gray-800 p-5 rounded-lg border-2 border-teal-500 my-6\">\n<h5 class=\"font-bold text-teal-600 dark:text-teal-400 mb-3\">üó∫Ô∏è Internal User ‚Üí Sandbox Mapping</h5>\n<p class=\"text-sm text-gray-600 dark:text-gray-300 mb-3\">Redis provides instant lookups between users and their isolated sandbox environments:</p>\n<ul class=\"text-sm space-y-2 text-gray-600 dark:text-gray-300\">\n<li>‚Ä¢ <strong>Privacy-aware</strong>: You don\'t need to worry about internal IPs or where your sandbox lives</li>\n<li>‚Ä¢ <strong>Scalable</strong>: Built to handle millions of concurrent containers</li>\n<li>‚Ä¢ <strong>Ephemeral</strong>: Sandboxes can be destroyed at will - your data stays yours</li>\n<li>‚Ä¢ <strong>Instant routing</strong>: Redis maps your session to your sandbox in microseconds</li>\n</ul>\n<pre class=\"text-xs bg-gray-900 text-green-400 p-3 rounded overflow-x-auto mt-4\"><code># How Ginto maps users to sandboxes\nHSET user:123 sandbox_id \"lxc-abc123\"\nHSET user:123 sandbox_ip \"10.0.0.42\"  # Internal - you never see this\nEXPIRE user:123 3600  # Auto-cleanup</code></pre>\n</div>\n\n<h4>üîÆ Future: Enhanced Monaco Editor State</h4>\n\n<p>While not yet implemented, Redis <em>could</em> enhance our Monaco code editor with real-time collaboration features:</p>\n\n<div class=\"bg-gray-100 dark:bg-gray-800 p-5 rounded-lg border border-gray-300 dark:border-gray-600 my-6 opacity-80\">\n<div class=\"flex items-center gap-2 mb-3\">\n<span class=\"text-lg\">&lt;/&gt;</span>\n<h5 class=\"font-bold text-gray-600 dark:text-gray-400\">Potential: Monaco Editor State</h5>\n<span class=\"text-xs bg-gray-200 dark:bg-gray-700 text-gray-600 dark:text-gray-400 px-2 py-0.5 rounded\">Coming Soon</span>\n</div>\n<p class=\"text-sm text-gray-600 dark:text-gray-400 mb-3\">Redis could enable real-time collaboration where multiple users edit the same file:</p>\n<ul class=\"text-sm space-y-1 text-gray-500 dark:text-gray-400\">\n<li>‚Ä¢ Cursor position sync</li>\n<li>‚Ä¢ File change broadcasts</li>\n<li>‚Ä¢ Undo/redo state stacks</li>\n<li>‚Ä¢ Syntax error caching</li>\n</ul>\n</div>\n\n<h3>The Big Picture</h3>\n\n<p>As you focus on building features with the AI agents we\'re developing, Ginto handles all the infrastructure complexity:</p>\n\n<div class=\"my-8\">\n<!-- Visual Architecture Diagram -->\n<div class=\"bg-gradient-to-b from-gray-900 to-gray-950 rounded-2xl p-8 border border-gray-700 overflow-x-auto\">\n\n<!-- Layer 1: You -->\n<div class=\"flex justify-center mb-6\">\n<div class=\"bg-gradient-to-r from-violet-600 to-purple-600 rounded-xl px-8 py-4 text-center shadow-lg shadow-violet-500/20 max-w-lg w-full\">\n<div class=\"flex items-center justify-center gap-2 text-white font-bold text-lg mb-1\">\n<i class=\"fas fa-user-astronaut\"></i>\n<span>YOU (Developer)</span>\n</div>\n<div class=\"text-violet-200 text-sm italic\">\"Build a REST API for my e-commerce app\"</div>\n</div>\n</div>\n\n<!-- Arrow 1 -->\n<div class=\"flex justify-center my-4\">\n<div class=\"flex flex-col items-center text-gray-500\">\n<div class=\"w-0.5 h-6 bg-gradient-to-b from-violet-500 to-teal-500\"></div>\n<i class=\"fas fa-chevron-down text-teal-500\"></i>\n</div>\n</div>\n\n<!-- Layer 2: Ginto -->\n<div class=\"flex justify-center mb-6\">\n<div class=\"bg-gradient-to-r from-teal-600 to-cyan-600 rounded-xl px-6 py-4 shadow-lg shadow-teal-500/20 max-w-2xl w-full\">\n<div class=\"text-center text-white font-bold mb-3\">\n<i class=\"fas fa-robot mr-2\"></i>GINTO + AI AGENTS\n</div>\n<div class=\"grid grid-cols-3 gap-3\">\n<div class=\"bg-white/10 rounded-lg px-3 py-2 text-center\">\n<div class=\"text-teal-200 text-xs uppercase tracking-wide\">Route</div>\n<div class=\"text-white text-sm font-medium\">Request</div>\n</div>\n<div class=\"bg-white/10 rounded-lg px-3 py-2 text-center\">\n<div class=\"text-teal-200 text-xs uppercase tracking-wide\">Execute</div>\n<div class=\"text-white text-sm font-medium\">Code</div>\n</div>\n<div class=\"bg-white/10 rounded-lg px-3 py-2 text-center\">\n<div class=\"text-teal-200 text-xs uppercase tracking-wide\">Stream</div>\n<div class=\"text-white text-sm font-medium\">Response</div>\n</div>\n</div>\n</div>\n</div>\n\n<!-- Arrow 2 -->\n<div class=\"flex justify-center my-4\">\n<div class=\"flex flex-col items-center text-gray-500\">\n<div class=\"w-0.5 h-6 bg-gradient-to-b from-teal-500 to-red-500\"></div>\n<i class=\"fas fa-chevron-down text-red-500\"></i>\n</div>\n</div>\n\n<!-- Layer 3: Redis -->\n<div class=\"flex justify-center mb-6\">\n<div class=\"bg-gradient-to-r from-red-600 to-rose-600 rounded-xl px-6 py-5 shadow-lg shadow-red-500/20 max-w-3xl w-full\">\n<div class=\"text-center text-white font-bold mb-4 flex items-center justify-center gap-2\">\n<svg class=\"w-5 h-5\" viewBox=\"0 0 32 32\" fill=\"currentColor\"><path d=\"M15.4 17.6c-4.1 1-10 .5-10 .5l10 4.5 10-4.5s-5.9.5-10-.5zm0-5.8c-4.1 1-10 .5-10 .5l10 4.5 10-4.5s-5.9.5-10-.5zm0-5.8c-4.1 1-10 .5-10 .5l10 4.5 10-4.5s-5.9.5-10-.5z\"/></svg>\nREDIS (Scalable User Mapping)\n</div>\n<div class=\"grid grid-cols-2 md:grid-cols-3 gap-3\">\n<div class=\"bg-gray-900/50 rounded-lg p-3 text-center border border-red-400/30\">\n<div class=\"text-2xl mb-1\">üó∫Ô∏è</div>\n<div class=\"text-white text-sm font-medium\">User ‚Üí Sandbox</div>\n<div class=\"text-red-300 text-xs\">Instant Lookup</div>\n</div>\n<div class=\"bg-gray-900/50 rounded-lg p-3 text-center border border-red-400/30\">\n<div class=\"text-2xl mb-1\">üîê</div>\n<div class=\"text-white text-sm font-medium\">Sessions</div>\n<div class=\"text-red-300 text-xs\">TTL-based</div>\n</div>\n<div class=\"bg-gray-900/50 rounded-lg p-3 text-center border border-red-400/30\">\n<div class=\"text-2xl mb-1\">üìà</div>\n<div class=\"text-white text-sm font-medium\">Millions Scale</div>\n<div class=\"text-red-300 text-xs\">Built for Growth</div>\n</div>\n</div>\n</div>\n</div>\n\n<!-- Arrow 3 -->\n<div class=\"flex justify-center my-4\">\n<div class=\"flex flex-col items-center text-gray-500\">\n<div class=\"w-0.5 h-6 bg-gradient-to-b from-red-500 to-emerald-500\"></div>\n<i class=\"fas fa-chevron-down text-emerald-500\"></i>\n</div>\n</div>\n\n<!-- Layer 4: Your Sandbox -->\n<div class=\"flex justify-center\">\n<div class=\"bg-gradient-to-r from-emerald-600 to-green-600 rounded-xl px-6 py-4 shadow-lg shadow-emerald-500/20 max-w-xl w-full\">\n<div class=\"text-center\">\n<div class=\"flex items-center justify-center gap-2 text-white font-bold mb-2\">\n<i class=\"fas fa-cube\"></i>\n<span>YOUR PRIVATE LXC SANDBOX</span>\n</div>\n<div class=\"flex flex-wrap justify-center gap-3 text-xs\">\n<span class=\"bg-white/20 px-2 py-1 rounded text-emerald-100\"><i class=\"fas fa-eye-slash mr-1\"></i>IP Hidden from You</span>\n<span class=\"bg-white/20 px-2 py-1 rounded text-emerald-100\"><i class=\"fas fa-lock mr-1\"></i>Isolated</span>\n<span class=\"bg-white/20 px-2 py-1 rounded text-emerald-100\"><i class=\"fas fa-trash-alt mr-1\"></i>Destroy at Will</span>\n</div>\n</div>\n</div>\n</div>\n\n</div>\n<div class=\"text-center text-sm text-gray-500 dark:text-gray-400 mt-3\">\n<strong>Figure:</strong> Redis enables instant user-to-sandbox routing without exposing infrastructure details\n</div>\n</div>\n\n<h3>What You\'ll Learn</h3>\n\n<p>This masterclass covers:</p>\n\n<ol>\n<li><strong>Installation</strong> - Setting up Redis on your system</li>\n<li><strong>Data Types</strong> - Working with strings, hashes, lists, sets, and sorted sets</li>\n<li><strong>Caching Patterns</strong> - Cache-aside, write-through, and invalidation strategies</li>\n<li><strong>Advanced Topics</strong> - Pub/Sub, transactions, Lua scripting, clustering</li>\n</ol>\n\n<!-- Open Source CTA -->\n<div class=\"bg-gradient-to-r from-gray-900 to-gray-800 rounded-xl p-6 my-8 border border-gray-700\">\n<div class=\"flex flex-col md:flex-row items-start md:items-center gap-4\">\n<div class=\"text-5xl\">üåç</div>\n<div class=\"flex-1\">\n<h4 class=\"font-bold text-white text-lg mb-2\">100% Open Source - Updated Daily</h4>\n<p class=\"text-gray-300 text-sm mb-3\">The entire Ginto platform is fully open source and shipped to GitHub daily. To learn technology, people need hands-on experience with precise, step-by-step documentation from beginner to advanced - the kind you can\'t find anywhere else.</p>\n<p class=\"text-gray-400 text-sm mb-3\">Purpose-built for collaboration and human progress in preparation for the intelligence explosion.</p>\n<p class=\"text-gray-300 text-sm\"><strong class=\"text-teal-400\">See how Redis is actually implemented in the cloud</strong> by looking at the architecture diagram in the README.</p>\n<a href=\"https://github.com/oliverbob/ginto.ai\" target=\"_blank\" class=\"inline-flex items-center gap-2 bg-white text-gray-900 px-4 py-2 rounded-lg font-semibold hover:bg-gray-100 transition-colors mt-4\">\n<i class=\"fab fa-github\"></i> View Source on GitHub\n</a>\n</div>\n</div>\n</div>\n\n<!-- Contribute CTA -->\n<div class=\"bg-gray-100 dark:bg-gray-800 rounded-xl p-6 my-6 border border-gray-200 dark:border-gray-700\">\n<div class=\"flex items-center gap-4\">\n<div class=\"text-4xl\">üõ†Ô∏è</div>\n<div>\n<h4 class=\"font-bold text-gray-900 dark:text-white mb-1\">Want to contribute?</h4>\n<p class=\"text-gray-600 dark:text-gray-400 text-sm\">Found an error? Have a better example? Submit a PR!</p>\n<a href=\"https://github.com/oliverbob/ginto.ai\" target=\"_blank\" class=\"inline-flex items-center gap-2 text-teal-600 dark:text-teal-400 hover:underline text-sm font-medium mt-2\">\n<i class=\"fab fa-github\"></i> Contribute on GitHub\n</a>\n</div>\n</div>\n</div>\n\n<div class=\"bg-gradient-to-r from-teal-500 to-cyan-500 text-white p-6 rounded-lg mt-8\">\n<h3 class=\"text-xl font-bold mb-2\">üöÄ Next: Installing Redis</h3>\n<p>Let\'s get Redis running on your system so you can follow along with hands-on examples.</p>\n</div>\n\n</div>',NULL,NULL,1,25,1,1,NULL,NULL,'2025-12-17 13:23:13','2025-12-17 18:46:11');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (8,1,'installation','Installing Redis','Install and configure Redis on various platforms.','text','<div class=\"lesson-content\">\n\n<h2>Installing Redis</h2>\n\n<p>Redis can be installed on virtually any system. This covers Linux, Docker, and cloud platforms.</p>\n\n<div class=\"info-box bg-teal-50 dark:bg-teal-900/20 border-l-4 border-teal-500 p-4 my-6 rounded-r\">\n<p class=\"font-semibold text-teal-700 dark:text-teal-300\">üí° Recommendation</p>\n<p class=\"text-teal-600 dark:text-teal-400\">For development, use Docker. For production, use managed Redis or install on Linux.</p>\n</div>\n\n<h3>Docker Installation (Recommended)</h3>\n\n<pre><code class=\"language-bash\"># Pull and run Redis with persistence\ndocker run -d \\\n  --name redis \\\n  -p 6379:6379 \\\n  -v redis-data:/data \\\n  redis:alpine redis-server --appendonly yes\n\n# Connect to Redis\ndocker exec -it redis redis-cli\n\n# With RedisInsight GUI\ndocker run -d \\\n  --name redis-stack \\\n  -p 6379:6379 \\\n  -p 8001:8001 \\\n  redis/redis-stack:latest\n\n# Access GUI at http://localhost:8001</code></pre>\n\n<h3>Ubuntu/Debian Installation</h3>\n\n<pre><code class=\"language-bash\"># Install Redis\nsudo apt update\nsudo apt install redis-server -y\n\n# Enable and start\nsudo systemctl enable redis-server\nsudo systemctl start redis-server\n\n# Test\nredis-cli ping\n# PONG</code></pre>\n\n<h3>Configuration</h3>\n\n<pre><code class=\"language-bash\"># /etc/redis/redis.conf\n\n# Network (security!)\nbind 127.0.0.1\n\n# Password\nrequirepass YourSecurePassword123!\n\n# Memory limit\nmaxmemory 256mb\nmaxmemory-policy allkeys-lru\n\n# Persistence\nappendonly yes</code></pre>\n\n<h3>PHP Connection</h3>\n\n<pre><code class=\"language-php\">&lt;?php\n// Install: composer require predis/predis\n\nrequire \'vendor/autoload.php\';\n\n$redis = new Predis\\Client([\n    \'scheme\' => \'tcp\',\n    \'host\'   => \'127.0.0.1\',\n    \'port\'   => 6379,\n    \'password\' => \'YourSecurePassword123!\'\n]);\n\necho $redis->ping(); // PONG\n\n$redis->set(\'name\', \'John\');\necho $redis->get(\'name\'); // John</code></pre>\n\n<h3>Verify Installation</h3>\n\n<pre><code class=\"language-bash\"># Check version\nredis-server --version\n\n# Server info\nredis-cli INFO server\n\n# Memory usage\nredis-cli INFO memory\n\n# Monitor commands\nredis-cli MONITOR</code></pre>\n\n<h3>Useful Resources</h3>\n\n<ul class=\"space-y-1\">\n<li>üìö <a href=\"https://redis.io/docs/getting-started/installation/\" class=\"text-teal-600 dark:text-teal-400 hover:underline\">Official Installation Guide</a></li>\n<li>üê≥ <a href=\"https://hub.docker.com/_/redis\" class=\"text-teal-600 dark:text-teal-400 hover:underline\">Redis Docker Hub</a></li>\n</ul>\n\n<div class=\"bg-gradient-to-r from-teal-500 to-cyan-500 text-white p-6 rounded-lg mt-8\">\n<h4 class=\"font-bold text-lg mb-2\">üöÄ Next Steps</h4>\n<p>In the next lesson, you\'ll master Redis data types.</p>\n</div>\n\n</div>',NULL,NULL,2,20,1,1,NULL,NULL,'2025-12-17 13:23:13','2025-12-17 17:33:37');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (9,3,'introduction','Introduction to Docker','Understanding containers and why Docker revolutionized software deployment.','text','<div class=\"prose dark:prose-invert max-w-none\">\n<h2>What is Docker?</h2>\n<p>Docker is a platform for developing, shipping, and running applications in containers‚Äîlightweight, standalone, executable packages that include everything needed to run an application.</p>\n\n<h3>Containers vs Virtual Machines</h3>\n<table class=\"min-w-full\">\n<thead><tr><th>Feature</th><th>Containers</th><th>VMs</th></tr></thead>\n<tbody>\n<tr><td>Startup Time</td><td>Seconds</td><td>Minutes</td></tr>\n<tr><td>Size</td><td>MBs</td><td>GBs</td></tr>\n<tr><td>Isolation</td><td>Process-level</td><td>Full OS</td></tr>\n<tr><td>Performance</td><td>Near-native</td><td>Overhead</td></tr>\n</tbody>\n</table>\n\n<h3>Docker Architecture</h3>\n<ul>\n<li><strong>Docker Engine</strong> - The runtime that runs containers</li>\n<li><strong>Docker Images</strong> - Read-only templates for containers</li>\n<li><strong>Docker Containers</strong> - Running instances of images</li>\n<li><strong>Docker Registry</strong> - Storage for images (Docker Hub)</li>\n</ul>\n</div>',NULL,NULL,1,15,1,1,NULL,NULL,'2025-12-17 13:23:13','2025-12-17 13:23:13');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (10,3,'installation','Installing Docker','Install Docker on Linux, macOS, and Windows.','text','<div class=\"prose dark:prose-invert max-w-none\">\n<h2>Installing Docker</h2>\n\n<h3>Ubuntu/Debian</h3>\n<pre class=\"bg-gray-900 text-green-400 p-4 rounded-lg overflow-x-auto\"><code># Remove old versions\nsudo apt remove docker docker-engine docker.io containerd runc\n\n# Install prerequisites\nsudo apt update\nsudo apt install ca-certificates curl gnupg lsb-release\n\n# Add Docker GPG key\nsudo mkdir -p /etc/apt/keyrings\ncurl -fsSL https://download.docker.com/linux/ubuntu/gpg | sudo gpg --dearmor -o /etc/apt/keyrings/docker.gpg\n\n# Add repository\necho \"deb [arch=$(dpkg --print-architecture) signed-by=/etc/apt/keyrings/docker.gpg] https://download.docker.com/linux/ubuntu $(lsb_release -cs) stable\" | sudo tee /etc/apt/sources.list.d/docker.list > /dev/null\n\n# Install Docker\nsudo apt update\nsudo apt install docker-ce docker-ce-cli containerd.io docker-compose-plugin\n\n# Add user to docker group\nsudo usermod -aG docker $USER</code></pre>\n</div>',NULL,NULL,2,15,1,1,NULL,NULL,'2025-12-17 13:23:13','2025-12-17 13:23:13');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (11,2,'introduction','Introduction to LXC/LXD','Understanding system containers and how they differ from application containers.','text','<div class=\"prose dark:prose-invert max-w-none\">\n<h2>What are System Containers?</h2>\n<p>LXC (Linux Containers) provides operating system-level virtualization. Unlike Docker (application containers), LXC containers run a full init system and behave like lightweight VMs.</p>\n\n<h3>LXC vs LXD</h3>\n<ul>\n<li><strong>LXC</strong> - The low-level container runtime (liblxc)</li>\n<li><strong>LXD</strong> - The modern container manager built on LXC, providing REST API, clustering, and image management</li>\n</ul>\n\n<h3>Why LXC/LXD?</h3>\n<ul>\n<li>Run multiple services in one container</li>\n<li>Full systemd support</li>\n<li>Persistent containers (not ephemeral)</li>\n<li>Easy migration between hosts</li>\n<li>Familiar VM-like experience</li>\n</ul>\n\n<h3>Use Cases</h3>\n<ol>\n<li>Development sandboxes (like Ginto AI!)</li>\n<li>CI/CD build environments</li>\n<li>Multi-tenant hosting</li>\n<li>Legacy application migration</li>\n</ol>\n</div>',NULL,NULL,1,15,1,1,NULL,NULL,'2025-12-17 13:23:13','2025-12-17 13:23:13');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (12,2,'installation','Installing LXD','Install and initialize LXD on Ubuntu.','text','<div class=\"prose dark:prose-invert max-w-none\">\n<h2>Installing LXD</h2>\n\n<h3>Install via Snap (Recommended)</h3>\n<pre class=\"bg-gray-900 text-green-400 p-4 rounded-lg overflow-x-auto\"><code># Install LXD\nsudo snap install lxd\n\n# Add your user to the lxd group\nsudo usermod -aG lxd $USER\nnewgrp lxd\n\n# Initialize LXD\nlxd init</code></pre>\n\n<h3>Initialization Options</h3>\n<p>The <code>lxd init</code> wizard will ask:</p>\n<ul>\n<li><strong>Clustering</strong> - No for single-node, Yes for HA</li>\n<li><strong>Storage backend</strong> - ZFS, BTRFS, or dir</li>\n<li><strong>Storage pool size</strong> - Default or specify size</li>\n<li><strong>Network</strong> - Create managed bridge (lxdbr0)</li>\n<li><strong>Remote access</strong> - For remote management</li>\n</ul>\n\n<h3>Quick Start (Non-Interactive)</h3>\n<pre class=\"bg-gray-900 text-green-400 p-4 rounded-lg overflow-x-auto\"><code>lxd init --auto</code></pre>\n</div>',NULL,NULL,2,15,1,1,NULL,NULL,'2025-12-17 13:23:13','2025-12-17 13:23:13');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (13,4,'introduction','Introduction to Proxmox VE','Overview of Proxmox Virtual Environment and its capabilities.','text','<div class=\"prose dark:prose-invert max-w-none\">\n<h2>What is Proxmox VE?</h2>\n<p>Proxmox Virtual Environment is a complete, open-source server management platform for enterprise virtualization. It integrates KVM hypervisor and LXC containers on a single platform.</p>\n\n<h3>Key Features</h3>\n<ul>\n<li><strong>KVM Virtualization</strong> - Full virtualization for any OS</li>\n<li><strong>LXC Containers</strong> - Lightweight Linux containers</li>\n<li><strong>Web UI</strong> - Complete management interface</li>\n<li><strong>Clustering</strong> - Multi-node high availability</li>\n<li><strong>Storage</strong> - ZFS, Ceph, NFS, iSCSI support</li>\n<li><strong>Backup</strong> - Built-in backup and restore</li>\n</ul>\n\n<h3>Why Proxmox?</h3>\n<ol>\n<li>Free and open-source (optional paid support)</li>\n<li>No license limits on features</li>\n<li>Enterprise-grade capabilities</li>\n<li>Active community</li>\n<li>REST API for automation</li>\n</ol>\n</div>',NULL,NULL,1,15,1,1,NULL,NULL,'2025-12-17 13:23:13','2025-12-17 13:23:13');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (14,5,'introduction','Introduction to Virtualmin','Overview of Virtualmin as a web hosting control panel.','text','<div class=\"prose dark:prose-invert max-w-none\">\n<h2>What is Virtualmin?</h2>\n<p>Virtualmin is a powerful, flexible web hosting control panel built on Webmin. It provides a complete solution for managing virtual hosts, email, databases, and more.</p>\n\n<h3>Virtualmin vs cPanel</h3>\n<table class=\"min-w-full\">\n<thead><tr><th>Feature</th><th>Virtualmin</th><th>cPanel</th></tr></thead>\n<tbody>\n<tr><td>Price</td><td>Free (GPL)</td><td>$15+/month</td></tr>\n<tr><td>Open Source</td><td>Yes</td><td>No</td></tr>\n<tr><td>OS Support</td><td>Multi-distro</td><td>CentOS/AlmaLinux</td></tr>\n<tr><td>Resource Usage</td><td>Lower</td><td>Higher</td></tr>\n</tbody>\n</table>\n\n<h3>Features</h3>\n<ul>\n<li>Virtual server (domain) management</li>\n<li>Apache/Nginx configuration</li>\n<li>Email hosting with spam filtering</li>\n<li>MySQL/PostgreSQL databases</li>\n<li>SSL certificate management</li>\n<li>Backup and restore</li>\n<li>Resource limits and quotas</li>\n</ul>\n</div>',NULL,NULL,1,15,1,1,NULL,NULL,'2025-12-17 13:23:13','2025-12-17 13:23:13');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (15,7,'introduction','Introduction to MVC','Understanding the Model-View-Controller pattern','text','<div class=\"lesson-content\">\n\n<h2>Introduction to MVC Architecture</h2>\n\n<p>The <strong>Model-View-Controller (MVC)</strong> pattern is the backbone of modern web applications. It separates your application into three interconnected components.</p>\n\n<div class=\"info-box bg-teal-50 dark:bg-teal-900/20 border-l-4 border-teal-500 p-4 my-6 rounded-r\">\n<p class=\"font-semibold text-teal-700 dark:text-teal-300\">üí° Ginto AI Native Support</p>\n<p class=\"text-teal-600 dark:text-teal-400\">Ginto AI natively supports MVC architecture, making it easy to generate Models, Controllers, and Views with proper patterns.</p>\n</div>\n\n<h3>Why MVC Matters</h3>\n\n<ul class=\"space-y-2\">\n<li><strong>Separation of Concerns</strong>: Each component has a single responsibility</li>\n<li><strong>Maintainability</strong>: Easier to update and debug</li>\n<li><strong>Testability</strong>: Components can be tested independently</li>\n<li><strong>Scalability</strong>: Teams can work on different parts simultaneously</li>\n</ul>\n\n<h3>The Three Components</h3>\n\n<div class=\"grid md:grid-cols-3 gap-4 my-6\">\n<div class=\"bg-gray-100 dark:bg-gray-800 p-4 rounded-lg\">\n<h4 class=\"font-bold text-teal-600 dark:text-teal-400\">üìä Model</h4>\n<p class=\"text-sm\">Represents your data and business logic. Handles database operations, validation, and data transformations.</p>\n</div>\n<div class=\"bg-gray-100 dark:bg-gray-800 p-4 rounded-lg\">\n<h4 class=\"font-bold text-cyan-600 dark:text-cyan-400\">üñºÔ∏è View</h4>\n<p class=\"text-sm\">Responsible for presentation. Displays data to users and handles the user interface.</p>\n</div>\n<div class=\"bg-gray-100 dark:bg-gray-800 p-4 rounded-lg\">\n<h4 class=\"font-bold text-violet-600 dark:text-violet-400\">üéÆ Controller</h4>\n<p class=\"text-sm\">Acts as the intermediary. Receives user input, processes it, and determines which Model and View to use.</p>\n</div>\n</div>\n\n<h3>How MVC Flow Works</h3>\n\n<pre><code class=\"language-plaintext\">‚îå‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îê     Request      ‚îå‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îê\n‚îÇ             ‚îÇ ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚ñ∫ ‚îÇ              ‚îÇ\n‚îÇ   Browser   ‚îÇ                  ‚îÇ  Controller  ‚îÇ\n‚îÇ             ‚îÇ ‚óÑ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ ‚îÇ              ‚îÇ\n‚îî‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îò     Response     ‚îî‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚î¨‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îò\n                                        ‚îÇ\n                         ‚îå‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îº‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îê\n                         ‚îÇ              ‚îÇ              ‚îÇ\n                         ‚ñº              ‚ñº              ‚ñº\n                   ‚îå‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îê   ‚îå‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îê   ‚îå‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îê\n                   ‚îÇ  Model  ‚îÇ   ‚îÇ  View   ‚îÇ   ‚îÇ Service ‚îÇ\n                   ‚îÇ (Data)  ‚îÇ   ‚îÇ (HTML)  ‚îÇ   ‚îÇ (Logic) ‚îÇ\n                   ‚îî‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îò   ‚îî‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îò   ‚îî‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îò</code></pre>\n\n<h3>Quick Start: Your First MVC Request</h3>\n\n<p>Let\'s trace a simple request through an MVC application:</p>\n\n<pre><code class=\"language-php\">// 1. User visits: https://yourapp.com/users/123\n\n// 2. Router matches the URL pattern\n$router->get(\'/users/{id}\', [UserController::class, \'show\']);\n\n// 3. Controller receives the request\nclass UserController {\n    public function show($id) {\n        // 4. Model fetches data\n        $user = $this->userModel->find($id);\n        \n        // 5. View renders the response\n        return view(\'users/show\', [\'user\' => $user]);\n    }\n}</code></pre>\n\n<h3>Real-World Example: Blog Application</h3>\n\n<pre><code class=\"language-php\">// Model: src/Models/Post.php\nclass Post {\n    public function getPublished(): array {\n        return $this->db->select(\'posts\', \'*\', [\n            \'status\' => \'published\',\n            \'ORDER\' => [\'created_at\' => \'DESC\']\n        ]);\n    }\n}\n\n// Controller: src/Controllers/BlogController.php  \nclass BlogController {\n    public function index() {\n        $posts = $this->postModel->getPublished();\n        return view(\'blog/index\', compact(\'posts\'));\n    }\n}\n\n// View: src/Views/blog/index.php\n&lt;?php foreach ($posts as $post): ?&gt;\n    &lt;article&gt;\n        &lt;h2&gt;&lt;?= htmlspecialchars($post[\'title\']) ?&gt;&lt;/h2&gt;\n        &lt;p&gt;&lt;?= htmlspecialchars($post[\'excerpt\']) ?&gt;&lt;/p&gt;\n    &lt;/article&gt;\n&lt;?php endforeach; ?&gt;</code></pre>\n\n<h3>MVC vs Other Patterns</h3>\n\n<table class=\"w-full text-sm\">\n<thead>\n<tr class=\"border-b dark:border-gray-700\">\n<th class=\"text-left p-2\">Pattern</th>\n<th class=\"text-left p-2\">Best For</th>\n<th class=\"text-left p-2\">Complexity</th>\n</tr>\n</thead>\n<tbody>\n<tr class=\"border-b dark:border-gray-700\">\n<td class=\"p-2\"><strong>MVC</strong></td>\n<td class=\"p-2\">Web applications, APIs</td>\n<td class=\"p-2\">Medium</td>\n</tr>\n<tr class=\"border-b dark:border-gray-700\">\n<td class=\"p-2\">MVP</td>\n<td class=\"p-2\">Desktop/Mobile apps</td>\n<td class=\"p-2\">Medium</td>\n</tr>\n<tr class=\"border-b dark:border-gray-700\">\n<td class=\"p-2\">MVVM</td>\n<td class=\"p-2\">Data-binding UIs</td>\n<td class=\"p-2\">High</td>\n</tr>\n</tbody>\n</table>\n\n<h3>Useful Resources</h3>\n\n<ul class=\"space-y-1\">\n<li>üìö <a href=\"https://en.wikipedia.org/wiki/Model%E2%80%93view%E2%80%93controller\" class=\"text-teal-600 dark:text-teal-400 hover:underline\">MVC on Wikipedia</a></li>\n<li>üìñ <a href=\"https://www.php.net/manual/en/\" class=\"text-teal-600 dark:text-teal-400 hover:underline\">PHP Documentation</a></li>\n<li>üéì <a href=\"/masterclass\" class=\"text-teal-600 dark:text-teal-400 hover:underline\">More Ginto Masterclasses</a></li>\n</ul>\n\n<div class=\"bg-gradient-to-r from-teal-500 to-cyan-500 text-white p-6 rounded-lg mt-8\">\n<h4 class=\"font-bold text-lg mb-2\">üöÄ Next Steps</h4>\n<p>In the next lesson, you\'ll learn how to organize your MVC project structure for maximum maintainability and scalability.</p>\n</div>\n\n</div>',NULL,NULL,1,25,1,1,NULL,NULL,'2025-12-17 13:49:47','2025-12-17 17:20:16');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (16,7,'project-structure','Project Structure','Organizing your MVC application','text','<div class=\"lesson-content\">\n\n<h2>MVC Project Structure</h2>\n\n<p>A well-organized project structure is crucial for maintainability. This lesson covers the standard directory layout used in professional PHP applications.</p>\n\n<div class=\"info-box bg-teal-50 dark:bg-teal-900/20 border-l-4 border-teal-500 p-4 my-6 rounded-r\">\n<p class=\"font-semibold text-teal-700 dark:text-teal-300\">üí° Pro Tip</p>\n<p class=\"text-teal-600 dark:text-teal-400\">Ginto AI follows this exact structure, so your knowledge transfers directly to real-world projects built with the platform.</p>\n</div>\n\n<h3>Recommended Directory Layout</h3>\n\n<pre><code class=\"language-plaintext\">project/\n‚îú‚îÄ‚îÄ src/                          # Application source code\n‚îÇ   ‚îú‚îÄ‚îÄ Controllers/              # Handle HTTP requests\n‚îÇ   ‚îÇ   ‚îú‚îÄ‚îÄ HomeController.php\n‚îÇ   ‚îÇ   ‚îú‚îÄ‚îÄ UserController.php\n‚îÇ   ‚îÇ   ‚îî‚îÄ‚îÄ Api/\n‚îÇ   ‚îÇ       ‚îî‚îÄ‚îÄ ApiController.php\n‚îÇ   ‚îú‚îÄ‚îÄ Models/                   # Data and business logic\n‚îÇ   ‚îÇ   ‚îú‚îÄ‚îÄ User.php\n‚îÇ   ‚îÇ   ‚îú‚îÄ‚îÄ Post.php\n‚îÇ   ‚îÇ   ‚îî‚îÄ‚îÄ Comment.php\n‚îÇ   ‚îú‚îÄ‚îÄ Views/                    # Templates and layouts\n‚îÇ   ‚îÇ   ‚îú‚îÄ‚îÄ layouts/\n‚îÇ   ‚îÇ   ‚îÇ   ‚îî‚îÄ‚îÄ main.php\n‚îÇ   ‚îÇ   ‚îú‚îÄ‚îÄ home/\n‚îÇ   ‚îÇ   ‚îÇ   ‚îî‚îÄ‚îÄ index.php\n‚îÇ   ‚îÇ   ‚îî‚îÄ‚îÄ user/\n‚îÇ   ‚îÇ       ‚îú‚îÄ‚îÄ index.php\n‚îÇ   ‚îÇ       ‚îî‚îÄ‚îÄ show.php\n‚îÇ   ‚îú‚îÄ‚îÄ Routes/                   # Route definitions\n‚îÇ   ‚îÇ   ‚îú‚îÄ‚îÄ web.php\n‚îÇ   ‚îÇ   ‚îî‚îÄ‚îÄ api.php\n‚îÇ   ‚îú‚îÄ‚îÄ Middleware/               # Request/Response filters\n‚îÇ   ‚îÇ   ‚îú‚îÄ‚îÄ AuthMiddleware.php\n‚îÇ   ‚îÇ   ‚îî‚îÄ‚îÄ CorsMiddleware.php\n‚îÇ   ‚îî‚îÄ‚îÄ Core/                     # Framework core\n‚îÇ       ‚îú‚îÄ‚îÄ Router.php\n‚îÇ       ‚îî‚îÄ‚îÄ Database.php\n‚îú‚îÄ‚îÄ public/                       # Web-accessible files\n‚îÇ   ‚îú‚îÄ‚îÄ index.php                 # Single entry point\n‚îÇ   ‚îî‚îÄ‚îÄ assets/\n‚îÇ       ‚îú‚îÄ‚îÄ css/\n‚îÇ       ‚îú‚îÄ‚îÄ js/\n‚îÇ       ‚îî‚îÄ‚îÄ images/\n‚îú‚îÄ‚îÄ config/                       # Configuration files\n‚îÇ   ‚îî‚îÄ‚îÄ database.php\n‚îú‚îÄ‚îÄ storage/                      # Generated files\n‚îÇ   ‚îú‚îÄ‚îÄ logs/\n‚îÇ   ‚îî‚îÄ‚îÄ cache/\n‚îú‚îÄ‚îÄ vendor/                       # Composer dependencies\n‚îú‚îÄ‚îÄ .env                          # Environment variables\n‚îî‚îÄ‚îÄ composer.json                 # Dependencies</code></pre>\n\n<h3>Key Principles</h3>\n\n<div class=\"grid md:grid-cols-2 gap-4 my-6\">\n<div class=\"bg-gray-100 dark:bg-gray-800 p-4 rounded-lg\">\n<h4 class=\"font-bold text-teal-600 dark:text-teal-400\">üö™ Single Entry Point</h4>\n<p class=\"text-sm\">All requests go through <code>public/index.php</code>. This allows centralized security and routing.</p>\n</div>\n<div class=\"bg-gray-100 dark:bg-gray-800 p-4 rounded-lg\">\n<h4 class=\"font-bold text-cyan-600 dark:text-cyan-400\">üì¶ PSR-4 Autoloading</h4>\n<p class=\"text-sm\">Use Composer\'s autoloader. No more <code>require</code> statements everywhere.</p>\n</div>\n<div class=\"bg-gray-100 dark:bg-gray-800 p-4 rounded-lg\">\n<h4 class=\"font-bold text-violet-600 dark:text-violet-400\">üîê Environment Config</h4>\n<p class=\"text-sm\">Keep secrets in <code>.env</code> files. Never commit credentials to Git.</p>\n</div>\n<div class=\"bg-gray-100 dark:bg-gray-800 p-4 rounded-lg\">\n<h4 class=\"font-bold text-violet-600 dark:text-violet-400\">üìÅ Asset Organization</h4>\n<p class=\"text-sm\">Static files in <code>public/assets/</code>. Version for cache busting.</p>\n</div>\n</div>\n\n<h3>The Entry Point: index.php</h3>\n\n<pre><code class=\"language-php\">&lt;?php\n// public/index.php\n\n// Load Composer autoloader\nrequire __DIR__ . \'/../vendor/autoload.php\';\n\n// Load environment variables\n$dotenv = Dotenv\\Dotenv::createImmutable(__DIR__ . \'/..\');\n$dotenv->load();\n\n// Initialize the application\n$app = new App\\Core\\Application();\n\n// Load routes\nrequire __DIR__ . \'/../src/Routes/web.php\';\n\n// Handle the request\n$app->run();</code></pre>\n\n<h3>Composer Configuration</h3>\n\n<pre><code class=\"language-json\">{\n    \"name\": \"yourapp/project\",\n    \"autoload\": {\n        \"psr-4\": {\n            \"App\\\\\": \"src/\"\n        }\n    },\n    \"require\": {\n        \"php\": \"^8.1\",\n        \"catfan/medoo\": \"^2.1\",\n        \"vlucas/phpdotenv\": \"^5.5\"\n    }\n}</code></pre>\n\n<h3>Environment Variables</h3>\n\n<pre><code class=\"language-bash\"># .env.example\nAPP_NAME=\"My Application\"\nAPP_ENV=local\nAPP_DEBUG=true\n\nDB_HOST=localhost\nDB_DATABASE=myapp\nDB_USERNAME=root\nDB_PASSWORD=\n\n# Never commit .env with real values!</code></pre>\n\n<h3>Creating Your First Project</h3>\n\n<pre><code class=\"language-bash\"># Create project directory\nmkdir myproject && cd myproject\n\n# Initialize Composer\ncomposer init\n\n# Install dependencies\ncomposer require catfan/medoo vlucas/phpdotenv\n\n# Create directory structure\nmkdir -p src/{Controllers,Models,Views,Routes,Core}\nmkdir -p public/assets/{css,js,images}\nmkdir -p config storage/{logs,cache}\n\n# Create entry point\ntouch public/index.php\n\n# Set permissions\nchmod -R 775 storage</code></pre>\n\n<div class=\"bg-gradient-to-r from-teal-500 to-cyan-500 text-white p-6 rounded-lg mt-8\">\n<h4 class=\"font-bold text-lg mb-2\">üöÄ Next Steps</h4>\n<p>In the next lesson, you\'ll learn how to build Models with the Medoo ORM for clean database operations.</p>\n</div>\n\n</div>',NULL,NULL,2,20,1,1,NULL,NULL,'2025-12-17 13:49:47','2025-12-17 17:33:37');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (17,7,'building-models','Building Models','Creating data models with Medoo ORM','text','<div class=\"lesson-content\">\n\n<h2>Building Models with Medoo ORM</h2>\n\n<p>Models handle your data layer. We use <strong>Medoo</strong> - a lightweight yet powerful PHP database framework.</p>\n\n<div class=\"info-box bg-teal-50 dark:bg-teal-900/20 border-l-4 border-teal-500 p-4 my-6 rounded-r\">\n<p class=\"font-semibold text-teal-700 dark:text-teal-300\">üí° Why Medoo?</p>\n<p class=\"text-teal-600 dark:text-teal-400\">Simple API, prevents SQL injection, supports MySQL/PostgreSQL/SQLite, and is natively integrated in Ginto AI.</p>\n</div>\n\n<h3>Installing Medoo</h3>\n\n<pre><code class=\"language-bash\"># Install via Composer\ncomposer require catfan/medoo</code></pre>\n\n<h3>Database Connection</h3>\n\n<pre><code class=\"language-php\">&lt;?php\n// config/database.php\n\nuse Medoo\\Medoo;\n\nreturn new Medoo([\n    \'type\' => \'mysql\',\n    \'host\' => $_ENV[\'DB_HOST\'],\n    \'database\' => $_ENV[\'DB_DATABASE\'],\n    \'username\' => $_ENV[\'DB_USERNAME\'],\n    \'password\' => $_ENV[\'DB_PASSWORD\'],\n    \'charset\' => \'utf8mb4\'\n]);</code></pre>\n\n<h3>Basic Model Structure</h3>\n\n<pre><code class=\"language-php\">&lt;?php\n// src/Models/User.php\n\nnamespace App\\Models;\n\nuse Medoo\\Medoo;\n\nclass User {\n    private Medoo $db;\n    protected string $table = \'users\';\n    \n    public function __construct(Medoo $db) {\n        $this->db = $db;\n    }\n    \n    public function find(int $id): ?array {\n        return $this->db->get($this->table, \'*\', [\'id\' => $id]);\n    }\n    \n    public function all(): array {\n        return $this->db->select($this->table, \'*\', [\n            \'ORDER\' => [\'created_at\' => \'DESC\']\n        ]);\n    }\n    \n    public function create(array $data): int {\n        $this->db->insert($this->table, [\n            \'name\' => $data[\'name\'],\n            \'email\' => $data[\'email\'],\n            \'password\' => password_hash($data[\'password\'], PASSWORD_DEFAULT),\n            \'created_at\' => date(\'Y-m-d H:i:s\')\n        ]);\n        return (int) $this->db->id();\n    }\n    \n    public function update(int $id, array $data): bool {\n        $result = $this->db->update($this->table, $data, [\'id\' => $id]);\n        return $result->rowCount() > 0;\n    }\n    \n    public function delete(int $id): bool {\n        $result = $this->db->delete($this->table, [\'id\' => $id]);\n        return $result->rowCount() > 0;\n    }\n}</code></pre>\n\n<h3>CRUD Operations Reference</h3>\n\n<table class=\"w-full text-sm my-6\">\n<thead>\n<tr class=\"border-b dark:border-gray-700 bg-gray-100 dark:bg-gray-800\">\n<th class=\"text-left p-3\">Operation</th>\n<th class=\"text-left p-3\">Medoo Method</th>\n<th class=\"text-left p-3\">Example</th>\n</tr>\n</thead>\n<tbody>\n<tr class=\"border-b dark:border-gray-700\">\n<td class=\"p-3\"><strong>Create</strong></td>\n<td class=\"p-3\"><code>insert()</code></td>\n<td class=\"p-3\"><code>$db->insert(\'users\', $data)</code></td>\n</tr>\n<tr class=\"border-b dark:border-gray-700\">\n<td class=\"p-3\"><strong>Read One</strong></td>\n<td class=\"p-3\"><code>get()</code></td>\n<td class=\"p-3\"><code>$db->get(\'users\', \'*\', [\'id\' => 1])</code></td>\n</tr>\n<tr class=\"border-b dark:border-gray-700\">\n<td class=\"p-3\"><strong>Read Many</strong></td>\n<td class=\"p-3\"><code>select()</code></td>\n<td class=\"p-3\"><code>$db->select(\'users\', \'*\')</code></td>\n</tr>\n<tr class=\"border-b dark:border-gray-700\">\n<td class=\"p-3\"><strong>Update</strong></td>\n<td class=\"p-3\"><code>update()</code></td>\n<td class=\"p-3\"><code>$db->update(\'users\', $data, [\'id\' => 1])</code></td>\n</tr>\n<tr class=\"border-b dark:border-gray-700\">\n<td class=\"p-3\"><strong>Delete</strong></td>\n<td class=\"p-3\"><code>delete()</code></td>\n<td class=\"p-3\"><code>$db->delete(\'users\', [\'id\' => 1])</code></td>\n</tr>\n</tbody>\n</table>\n\n<h3>Advanced Queries</h3>\n\n<pre><code class=\"language-php\">// Where conditions\n$activeUsers = $db->select(\'users\', \'*\', [\n    \'status\' => \'active\',\n    \'role\' => [\'admin\', \'moderator\'],  // IN clause\n    \'age[>=]\' => 18,                   // Comparison\n    \'name[~]\' => \'%john%\'              // LIKE\n]);\n\n// Ordering and limiting\n$latestUsers = $db->select(\'users\', \'*\', [\n    \'ORDER\' => [\'created_at\' => \'DESC\'],\n    \'LIMIT\' => 10\n]);\n\n// Joins\n$posts = $db->select(\'posts\', [\n    \'[>]users\' => [\'user_id\' => \'id\']  // LEFT JOIN\n], [\n    \'posts.id\',\n    \'posts.title\',\n    \'users.name(author)\'\n]);\n\n// Aggregations\n$count = $db->count(\'users\', [\'status\' => \'active\']);\n$maxId = $db->max(\'users\', \'id\');</code></pre>\n\n<h3>Useful Resources</h3>\n\n<ul class=\"space-y-1\">\n<li>üìö <a href=\"https://medoo.in/doc\" class=\"text-teal-600 dark:text-teal-400 hover:underline\">Medoo Official Documentation</a></li>\n<li>üìñ <a href=\"https://medoo.in/api/select\" class=\"text-teal-600 dark:text-teal-400 hover:underline\">Select Query Reference</a></li>\n</ul>\n\n<div class=\"bg-gradient-to-r from-teal-500 to-cyan-500 text-white p-6 rounded-lg mt-8\">\n<h4 class=\"font-bold text-lg mb-2\">üöÄ Next Steps</h4>\n<p>In the next lesson, you\'ll learn how to create Controllers and set up routing.</p>\n</div>\n\n</div>',NULL,NULL,3,30,1,1,NULL,NULL,'2025-12-17 13:49:47','2025-12-17 17:23:17');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (18,7,'controllers-routing','Controllers & Routing','Handling requests with controllers','text','<div class=\"lesson-content\">\n\n<h2>Controllers & Routing</h2>\n\n<p>Controllers receive HTTP requests and coordinate responses. Combined with routing, they form the backbone of request handling.</p>\n\n<div class=\"info-box bg-teal-50 dark:bg-teal-900/20 border-l-4 border-teal-500 p-4 my-6 rounded-r\">\n<p class=\"font-semibold text-teal-700 dark:text-teal-300\">üí° RESTful Design</p>\n<p class=\"text-teal-600 dark:text-teal-400\">Following RESTful conventions makes your API predictable. Ginto AI generates RESTful controllers by default.</p>\n</div>\n\n<h3>Basic Controller Structure</h3>\n\n<pre><code class=\"language-php\">&lt;?php\n// src/Controllers/UserController.php\n\nnamespace App\\Controllers;\n\nuse App\\Models\\User;\n\nclass UserController {\n    private User $userModel;\n    \n    public function __construct(User $userModel) {\n        $this->userModel = $userModel;\n    }\n    \n    // GET /users - List all users\n    public function index(): string {\n        $users = $this->userModel->all();\n        return view(\'users/index\', [\'users\' => $users]);\n    }\n    \n    // GET /users/{id} - Show single user\n    public function show(int $id): string {\n        $user = $this->userModel->find($id);\n        \n        if (!$user) {\n            http_response_code(404);\n            return view(\'errors/404\');\n        }\n        \n        return view(\'users/show\', [\'user\' => $user]);\n    }\n    \n    // POST /users - Store new user\n    public function store(): void {\n        $data = [\n            \'name\' => $_POST[\'name\'] ?? \'\',\n            \'email\' => $_POST[\'email\'] ?? \'\',\n            \'password\' => $_POST[\'password\'] ?? \'\'\n        ];\n        \n        $userId = $this->userModel->create($data);\n        \n        header(\"Location: /users/{$userId}\");\n        exit;\n    }\n}</code></pre>\n\n<h3>RESTful Route Conventions</h3>\n\n<table class=\"w-full text-sm my-6\">\n<thead>\n<tr class=\"border-b dark:border-gray-700 bg-gray-100 dark:bg-gray-800\">\n<th class=\"text-left p-3\">Method</th>\n<th class=\"text-left p-3\">URI</th>\n<th class=\"text-left p-3\">Action</th>\n<th class=\"text-left p-3\">Description</th>\n</tr>\n</thead>\n<tbody>\n<tr class=\"border-b dark:border-gray-700\">\n<td class=\"p-3\"><code>GET</code></td>\n<td class=\"p-3\">/users</td>\n<td class=\"p-3\">index</td>\n<td class=\"p-3\">List all</td>\n</tr>\n<tr class=\"border-b dark:border-gray-700\">\n<td class=\"p-3\"><code>GET</code></td>\n<td class=\"p-3\">/users/create</td>\n<td class=\"p-3\">create</td>\n<td class=\"p-3\">Show form</td>\n</tr>\n<tr class=\"border-b dark:border-gray-700\">\n<td class=\"p-3\"><code>POST</code></td>\n<td class=\"p-3\">/users</td>\n<td class=\"p-3\">store</td>\n<td class=\"p-3\">Save new</td>\n</tr>\n<tr class=\"border-b dark:border-gray-700\">\n<td class=\"p-3\"><code>GET</code></td>\n<td class=\"p-3\">/users/{id}</td>\n<td class=\"p-3\">show</td>\n<td class=\"p-3\">Show one</td>\n</tr>\n<tr class=\"border-b dark:border-gray-700\">\n<td class=\"p-3\"><code>PUT</code></td>\n<td class=\"p-3\">/users/{id}</td>\n<td class=\"p-3\">update</td>\n<td class=\"p-3\">Update</td>\n</tr>\n<tr class=\"border-b dark:border-gray-700\">\n<td class=\"p-3\"><code>DELETE</code></td>\n<td class=\"p-3\">/users/{id}</td>\n<td class=\"p-3\">destroy</td>\n<td class=\"p-3\">Delete</td>\n</tr>\n</tbody>\n</table>\n\n<h3>Route Definitions</h3>\n\n<pre><code class=\"language-php\">&lt;?php\n// src/Routes/web.php\n\nuse App\\Controllers\\HomeController;\nuse App\\Controllers\\UserController;\n\n// Home routes\n$router->get(\'/\', [HomeController::class, \'index\']);\n\n// User routes (RESTful)\n$router->get(\'/users\', [UserController::class, \'index\']);\n$router->get(\'/users/create\', [UserController::class, \'create\']);\n$router->post(\'/users\', [UserController::class, \'store\']);\n$router->get(\'/users/{id}\', [UserController::class, \'show\']);\n$router->put(\'/users/{id}\', [UserController::class, \'update\']);\n$router->delete(\'/users/{id}\', [UserController::class, \'destroy\']);</code></pre>\n\n<h3>API Controller Example</h3>\n\n<pre><code class=\"language-php\">&lt;?php\n// src/Controllers/Api/UserApiController.php\n\nclass UserApiController {\n    public function index(): void {\n        $users = $this->userModel->all();\n        \n        header(\'Content-Type: application/json\');\n        echo json_encode([\n            \'success\' => true,\n            \'data\' => $users\n        ]);\n    }\n    \n    public function show(int $id): void {\n        $user = $this->userModel->find($id);\n        \n        if (!$user) {\n            http_response_code(404);\n            echo json_encode([\'error\' => \'Not found\']);\n            return;\n        }\n        \n        header(\'Content-Type: application/json\');\n        echo json_encode([\'success\' => true, \'data\' => $user]);\n    }\n}</code></pre>\n\n<h3>Useful Resources</h3>\n\n<ul class=\"space-y-1\">\n<li>üìö <a href=\"https://restfulapi.net/\" class=\"text-teal-600 dark:text-teal-400 hover:underline\">RESTful API Design Guide</a></li>\n<li>üìñ <a href=\"https://www.php-fig.org/psr/psr-7/\" class=\"text-teal-600 dark:text-teal-400 hover:underline\">PSR-7: HTTP Message Interface</a></li>\n</ul>\n\n<div class=\"bg-gradient-to-r from-teal-500 to-cyan-500 text-white p-6 rounded-lg mt-8\">\n<h4 class=\"font-bold text-lg mb-2\">üöÄ Next Steps</h4>\n<p>In the next lesson, you\'ll learn how to create Views and implement templating.</p>\n</div>\n\n</div>',NULL,NULL,4,30,1,1,NULL,NULL,'2025-12-17 13:49:47','2025-12-17 17:23:17');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (19,7,'views-templating','Views & Templating','Building dynamic views with PHP templates','text','<h1>Views & Templating</h1>\n<p>Views handle the presentation layer of your application.</p>\n\n<h2>PHP Templates</h2>\n<pre><code class=\"language-php\">&lt;!-- views/users/index.php --&gt;\n&lt;h1&gt;All Users&lt;/h1&gt;\n\n&lt;?php foreach ($users as $user): ?&gt;\n&lt;div class=\"user-card\"&gt;\n    &lt;h2&gt;&lt;?= htmlspecialchars($user[\"name\"]) ?&gt;&lt;/h2&gt;\n    &lt;p&gt;&lt;?= htmlspecialchars($user[\"email\"]) ?&gt;&lt;/p&gt;\n&lt;/div&gt;\n&lt;?php endforeach; ?&gt;</code></pre>\n\n<h2>Layouts & Partials</h2>\n<pre><code class=\"language-php\">&lt;!-- views/layouts/main.php --&gt;\n&lt;?php include \"parts/head.php\"; ?&gt;\n&lt;?php include \"parts/sidebar.php\"; ?&gt;\n&lt;main&gt;&lt;?= $content ?&gt;&lt;/main&gt;\n&lt;?php include \"parts/footer.php\"; ?&gt;</code></pre>\n\n<h2>Security Best Practices</h2>\n<ul>\n<li>Always escape output with htmlspecialchars()</li>\n<li>Validate and sanitize user input</li>\n<li>Use CSRF tokens for forms</li>\n</ul>\n\n<p><strong>Ginto AI</strong> uses this exact templating approach with Tailwind CSS for styling.</p>',NULL,NULL,5,15,0,1,NULL,NULL,'2025-12-17 13:49:47','2025-12-17 13:49:47');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (20,7,'database-integration','Database Integration','Connecting to MySQL with Medoo','text','<h1>Database Integration</h1>\n<p>Connect your MVC application to MySQL using Medoo ORM.</p>\n\n<h2>Database Configuration</h2>\n<pre><code class=\"language-php\">// config/database.php\n&lt;?php\nuse Medoo\\Medoo;\n\n$db = new Medoo([\n    \"type\" => \"mysql\",\n    \"host\" => $_ENV[\"DB_HOST\"],\n    \"database\" => $_ENV[\"DB_NAME\"],\n    \"username\" => $_ENV[\"DB_USER\"],\n    \"password\" => $_ENV[\"DB_PASS\"],\n    \"charset\" => \"utf8mb4\"\n]);</code></pre>\n\n<h2>Environment Variables</h2>\n<pre><code># .env\nDB_HOST=localhost\nDB_NAME=myapp\nDB_USER=root\nDB_PASS=secret</code></pre>\n\n<h2>Query Examples</h2>\n<pre><code class=\"language-php\">// Select with conditions\n$users = $db->select(\"users\", \"*\", [\n    \"status\" => \"active\",\n    \"ORDER\" => [\"created_at\" => \"DESC\"],\n    \"LIMIT\" => 10\n]);\n\n// Join tables\n$posts = $db->select(\"posts\", [\n    \"[>]users\" => [\"author_id\" => \"id\"]\n], [\n    \"posts.title\",\n    \"users.name(author)\"\n]);</code></pre>\n\n<p><strong>Ginto AI natively supports</strong> MySQL and Medoo for all database operations.</p>',NULL,NULL,6,18,0,1,NULL,NULL,'2025-12-17 13:49:47','2025-12-17 13:49:47');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (21,7,'restful-apis','RESTful APIs','Building REST APIs with PHP','text','<h1>Building RESTful APIs</h1>\n<p>Create REST APIs following industry standards.</p>\n\n<h2>API Controller</h2>\n<pre><code class=\"language-php\">&lt;?php\nclass ApiController {\n    public function users() {\n        header(\"Content-Type: application/json\");\n        $users = $this->userModel->all();\n        echo json_encode([\"data\" => $users]);\n    }\n    \n    public function createUser() {\n        $data = json_decode(file_get_contents(\"php://input\"), true);\n        $user = $this->userModel->create($data);\n        http_response_code(201);\n        echo json_encode([\"data\" => $user]);\n    }\n}</code></pre>\n\n<h2>API Routes</h2>\n<pre><code class=\"language-php\">// API endpoints\n$router->get(\"/api/users\", [ApiController::class, \"users\"]);\n$router->post(\"/api/users\", [ApiController::class, \"createUser\"]);\n$router->get(\"/api/users/{id}\", [ApiController::class, \"getUser\"]);\n$router->put(\"/api/users/{id}\", [ApiController::class, \"updateUser\"]);\n$router->delete(\"/api/users/{id}\", [ApiController::class, \"deleteUser\"]);</code></pre>\n\n<h2>Response Helpers</h2>\n<pre><code class=\"language-php\">function jsonResponse($data, $status = 200) {\n    http_response_code($status);\n    header(\"Content-Type: application/json\");\n    echo json_encode($data);\n    exit;\n}</code></pre>\n\n<p><strong>Ginto AI</strong> provides API scaffolding and can help design your REST endpoints.</p>',NULL,NULL,7,20,0,1,NULL,NULL,'2025-12-17 13:49:47','2025-12-17 13:49:47');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (22,7,'authentication','Authentication','Implementing secure user authentication','text','<h1>Authentication</h1>\n<p>Implement secure user authentication in your MVC application.</p>\n\n<h2>Password Hashing</h2>\n<pre><code class=\"language-php\">// Registration\n$hashedPassword = password_hash($password, PASSWORD_DEFAULT);\n$db->insert(\"users\", [\n    \"email\" => $email,\n    \"password\" => $hashedPassword\n]);\n\n// Login verification\n$user = $db->get(\"users\", \"*\", [\"email\" => $email]);\nif (password_verify($password, $user[\"password\"])) {\n    // Success\n}</code></pre>\n\n<h2>Session Management</h2>\n<pre><code class=\"language-php\">session_start();\n\n// After successful login\n$_SESSION[\"user_id\"] = $user[\"id\"];\n$_SESSION[\"user_role\"] = $user[\"role\"];\n\n// Check authentication\nfunction isAuthenticated() {\n    return isset($_SESSION[\"user_id\"]);\n}\n\n// Logout\nsession_destroy();</code></pre>\n\n<h2>Middleware Pattern</h2>\n<pre><code class=\"language-php\">function authMiddleware($next) {\n    if (!isAuthenticated()) {\n        return redirect(\"/login\");\n    }\n    return $next();\n}</code></pre>\n\n<p><strong>Ginto AI</strong> handles authentication automatically and can help implement custom auth flows.</p>',NULL,NULL,8,22,0,1,NULL,NULL,'2025-12-17 13:49:47','2025-12-17 13:49:47');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (25,1,'clustering','Redis Clustering','Scaling Redis with clusters','text','<h1>Redis Clustering</h1>\n<p>Scale Redis horizontally with clustering for high availability.</p>\n\n<h2>Cluster Architecture</h2>\n<ul>\n<li>Data sharded across multiple nodes</li>\n<li>Automatic failover with replicas</li>\n<li>16384 hash slots distributed evenly</li>\n</ul>\n\n<h2>Creating a Cluster</h2>\n<pre><code>redis-cli --cluster create \\\n  127.0.0.1:7000 127.0.0.1:7001 127.0.0.1:7002 \\\n  127.0.0.1:7003 127.0.0.1:7004 127.0.0.1:7005 \\\n  --cluster-replicas 1</code></pre>\n\n<h2>Connecting to Cluster</h2>\n<pre><code class=\"language-php\">$redis = new Predis\\Client([\n    \"tcp://127.0.0.1:7000\",\n    \"tcp://127.0.0.1:7001\",\n    \"tcp://127.0.0.1:7002\"\n], [\"cluster\" => \"redis\"]);</code></pre>\n\n<p><strong>Ginto AI</strong> supports Redis clusters for enterprise deployments.</p>',NULL,NULL,5,15,0,1,NULL,NULL,'2025-12-17 13:50:48','2025-12-17 13:50:48');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (32,2,'container-management','Container Management','Creating and managing containers','text','<h1>Container Management</h1>\n\n<h2>Create Containers</h2>\n<pre><code>lxc launch ubuntu:22.04 mycontainer\nlxc launch images:alpine/3.18 alpine-dev</code></pre>\n\n<h2>Operations</h2>\n<pre><code>lxc list\nlxc start mycontainer\nlxc stop mycontainer\nlxc delete mycontainer --force</code></pre>\n\n<h2>Execute Commands</h2>\n<pre><code>lxc exec mycontainer -- bash\nlxc exec mycontainer -- apt update</code></pre>\n\n<p><strong>Ginto AI sandbox</strong> uses these commands for code isolation.</p>',NULL,NULL,3,18,0,1,NULL,NULL,'2025-12-17 13:52:23','2025-12-17 13:52:23');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (33,2,'networking-storage','Networking & Storage','Configure networks and volumes','text','<h1>Networking & Storage</h1>\n\n<h2>Port Forwarding</h2>\n<pre><code>lxc config device add mycontainer http proxy \\\n  listen=tcp:0.0.0.0:8080 connect=tcp:127.0.0.1:80</code></pre>\n\n<h2>Storage Volumes</h2>\n<pre><code>lxc storage volume create mypool data\nlxc config device add mycontainer data disk \\\n  pool=mypool source=data path=/data</code></pre>\n\n<h2>Resource Limits</h2>\n<pre><code>lxc config set mycontainer limits.cpu 2\nlxc config set mycontainer limits.memory 1GB</code></pre>\n\n<p><strong>Ginto AI</strong> uses resource limits for fair sandbox usage.</p>',NULL,NULL,4,20,0,1,NULL,NULL,'2025-12-17 13:52:23','2025-12-17 13:52:23');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (34,2,'profiles-security','Profiles & Security','Reusable configs and security','text','<h1>Profiles & Security</h1>\n\n<h2>Create Profile</h2>\n<pre><code>lxc profile create dev-profile</code></pre>\n\n<h2>Profile YAML</h2>\n<pre><code>config:\n  limits.cpu: \"2\"\n  limits.memory: 2GB\n  security.nesting: \"true\"</code></pre>\n\n<h2>Security Best Practices</h2>\n<ul>\n<li>Use unprivileged containers (default)</li>\n<li>Enable user namespaces</li>\n<li>Set resource quotas</li>\n</ul>\n\n<p><strong>Ginto AI</strong> runs all code in unprivileged, limited containers.</p>',NULL,NULL,5,15,0,1,NULL,NULL,'2025-12-17 13:52:23','2025-12-17 13:52:23');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (35,3,'docker-images','Docker Images','Working with Docker images','text','<h1>Docker Images</h1>\n\n<h2>Pull Images</h2>\n<pre><code>docker pull nginx\ndocker pull php:8.2-fpm\ndocker pull mysql:8.0</code></pre>\n\n<h2>List Images</h2>\n<pre><code>docker images\ndocker image ls</code></pre>\n\n<h2>Build from Dockerfile</h2>\n<pre><code>docker build -t myapp:1.0 .\ndocker build -t myapp:latest -f Dockerfile.prod .</code></pre>\n\n<p><strong>Ginto AI</strong> provides pre-built images for common development stacks.</p>',NULL,NULL,3,15,0,1,NULL,NULL,'2025-12-17 13:52:23','2025-12-17 13:52:23');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (36,3,'container-operations','Container Operations','Running and managing containers','text','<h1>Container Operations</h1>\n\n<h2>Run Containers</h2>\n<pre><code>docker run -d --name web nginx\ndocker run -it ubuntu bash\ndocker run -d -p 8080:80 nginx</code></pre>\n\n<h2>Manage Containers</h2>\n<pre><code>docker ps\ndocker ps -a\ndocker stop web\ndocker start web\ndocker rm web</code></pre>\n\n<h2>Logs & Exec</h2>\n<pre><code>docker logs web\ndocker logs -f web\ndocker exec -it web bash</code></pre>\n\n<p><strong>Ginto AI</strong> natively supports Docker for containerized deployments.</p>',NULL,NULL,4,18,0,1,NULL,NULL,'2025-12-17 13:52:23','2025-12-17 13:52:23');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (37,3,'docker-compose','Docker Compose','Multi-container applications','text','<h1>Docker Compose</h1>\n\n<h2>docker-compose.yml</h2>\n<pre><code>version: \"3.8\"\nservices:\n  web:\n    image: nginx\n    ports:\n      - \"80:80\"\n    volumes:\n      - ./html:/usr/share/nginx/html\n  \n  php:\n    image: php:8.2-fpm\n    volumes:\n      - ./src:/var/www/html\n  \n  db:\n    image: mysql:8.0\n    environment:\n      MYSQL_ROOT_PASSWORD: secret</code></pre>\n\n<h2>Commands</h2>\n<pre><code>docker-compose up -d\ndocker-compose down\ndocker-compose logs -f</code></pre>\n\n<p><strong>Ginto AI</strong> uses Compose for development environment orchestration.</p>',NULL,NULL,5,20,0,1,NULL,NULL,'2025-12-17 13:52:23','2025-12-17 13:52:23');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (38,3,'volumes-networks','Volumes & Networks','Persistent storage and networking','text','<h1>Volumes & Networks</h1>\n\n<h2>Volumes</h2>\n<pre><code># Create volume\ndocker volume create mydata\n\n# Use in container\ndocker run -v mydata:/data myapp\n\n# Bind mount\ndocker run -v $(pwd)/src:/app myapp</code></pre>\n\n<h2>Networks</h2>\n<pre><code># Create network\ndocker network create mynet\n\n# Run on network\ndocker run --network mynet --name web nginx\ndocker run --network mynet --name db mysql</code></pre>\n\n<p><strong>Ginto AI</strong> uses Docker networks for service discovery.</p>',NULL,NULL,6,15,0,1,NULL,NULL,'2025-12-17 13:52:23','2025-12-17 13:52:23');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (39,3,'dockerfile-best-practices','Dockerfile Best Practices','Writing efficient Dockerfiles','text','<h1>Dockerfile Best Practices</h1>\n\n<h2>Optimized Dockerfile</h2>\n<pre><code>FROM php:8.2-fpm-alpine\n\n# Install dependencies first (cached)\nRUN apk add --no-cache git curl\n\n# Copy composer files first\nCOPY composer.json composer.lock ./\nRUN composer install --no-dev\n\n# Copy source last\nCOPY src/ ./src/\n\n# Set permissions\nRUN chown -R www-data:www-data /app\n\nEXPOSE 9000\nCMD [\"php-fpm\"]</code></pre>\n\n<h2>Key Practices</h2>\n<ul>\n<li>Use Alpine images for smaller size</li>\n<li>Order layers by change frequency</li>\n<li>Combine RUN commands</li>\n<li>Use .dockerignore</li>\n</ul>\n\n<p><strong>Ginto AI</strong> generates optimized Dockerfiles for your stack.</p>',NULL,NULL,7,18,0,1,NULL,NULL,'2025-12-17 13:52:23','2025-12-17 13:52:23');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (40,4,'installation','Proxmox Installation','Installing Proxmox VE','text','<h1>Installing Proxmox VE</h1>\n\n<h2>Requirements</h2>\n<ul>\n<li>64-bit CPU with virtualization support</li>\n<li>Minimum 4GB RAM (16GB+ recommended)</li>\n<li>SSD storage recommended</li>\n</ul>\n\n<h2>Installation Steps</h2>\n<ol>\n<li>Download Proxmox VE ISO</li>\n<li>Create bootable USB</li>\n<li>Boot and follow installer</li>\n<li>Configure network and password</li>\n<li>Access web UI at https://your-ip:8006</li>\n</ol>\n\n<p><strong>Ginto AI</strong> can be deployed on Proxmox for enterprise installations.</p>',NULL,NULL,2,15,1,1,NULL,NULL,'2025-12-17 13:52:23','2025-12-17 13:52:23');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (41,4,'virtual-machines','Virtual Machines','Creating and managing VMs','text','<h1>Virtual Machines</h1>\n\n<h2>Create VM</h2>\n<ol>\n<li>Click \"Create VM\"</li>\n<li>Set VM ID and name</li>\n<li>Select ISO image</li>\n<li>Configure disk, CPU, memory</li>\n<li>Set network</li>\n</ol>\n\n<h2>VM Operations</h2>\n<ul>\n<li><strong>Start/Stop</strong>: Power controls</li>\n<li><strong>Snapshot</strong>: Save state</li>\n<li><strong>Clone</strong>: Duplicate VM</li>\n<li><strong>Migrate</strong>: Move to another node</li>\n</ul>\n\n<p><strong>Ginto AI</strong> natively supports Proxmox for VM-based deployments.</p>',NULL,NULL,3,18,0,1,NULL,NULL,'2025-12-17 13:52:23','2025-12-17 13:52:23');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (42,4,'lxc-containers','LXC on Proxmox','Running containers on Proxmox','text','<h1>LXC on Proxmox</h1>\n\n<h2>Create Container</h2>\n<ol>\n<li>Download CT template</li>\n<li>Click \"Create CT\"</li>\n<li>Configure: ID, hostname, password</li>\n<li>Set resources: CPU, memory, disk</li>\n<li>Configure network</li>\n</ol>\n\n<h2>Templates</h2>\n<pre><code># Download in shell\npveam update\npveam download local ubuntu-22.04-standard</code></pre>\n\n<h2>Container Features</h2>\n<ul>\n<li>Nesting (for Docker inside LXC)</li>\n<li>Unprivileged containers</li>\n<li>Resource limits</li>\n</ul>\n\n<p><strong>Ginto AI sandbox</strong> works on Proxmox LXC containers.</p>',NULL,NULL,4,20,0,1,NULL,NULL,'2025-12-17 13:52:23','2025-12-17 13:52:23');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (43,4,'storage-networking','Storage & Networking','Proxmox storage and network config','text','<h1>Storage & Networking</h1>\n\n<h2>Storage Types</h2>\n<ul>\n<li><strong>Local</strong>: Direct attached storage</li>\n<li><strong>NFS</strong>: Network file shares</li>\n<li><strong>iSCSI</strong>: Block storage</li>\n<li><strong>Ceph</strong>: Distributed storage</li>\n<li><strong>ZFS</strong>: Advanced local storage</li>\n</ul>\n\n<h2>Network Configuration</h2>\n<pre><code># /etc/network/interfaces\nauto vmbr0\niface vmbr0 inet static\n    address 192.168.1.100/24\n    gateway 192.168.1.1\n    bridge-ports eth0</code></pre>\n\n<h2>VLANs</h2>\n<pre><code>auto vmbr0.100\niface vmbr0.100 inet static\n    address 10.0.100.1/24</code></pre>\n\n<p><strong>Ginto AI</strong> supports various Proxmox network configurations.</p>',NULL,NULL,5,18,0,1,NULL,NULL,'2025-12-17 13:52:23','2025-12-17 13:52:23');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (44,4,'backup-ha','Backup & HA','Backups and high availability','text','<h1>Backup & High Availability</h1>\n\n<h2>Backup Options</h2>\n<ul>\n<li><strong>Snapshot</strong>: Quick, VM remains running</li>\n<li><strong>Suspend</strong>: Consistent state</li>\n<li><strong>Stop</strong>: Most consistent</li>\n</ul>\n\n<h2>Backup Schedule</h2>\n<pre><code># Datacenter > Backup\n- Mode: Snapshot\n- Storage: backup-storage\n- Schedule: 0 2 * * * (2 AM daily)</code></pre>\n\n<h2>High Availability</h2>\n<ol>\n<li>Create Proxmox cluster</li>\n<li>Configure shared storage</li>\n<li>Add resources to HA groups</li>\n<li>Set failover policies</li>\n</ol>\n\n<p><strong>Ginto AI</strong> can leverage Proxmox HA for zero-downtime deployments.</p>',NULL,NULL,6,15,0,1,NULL,NULL,'2025-12-17 13:52:23','2025-12-17 13:52:23');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (45,5,'installation','Virtualmin Installation','Installing Virtualmin','text','<h1>Installing Virtualmin</h1>\n\n<h2>Supported OS</h2>\n<ul>\n<li>Ubuntu 20.04/22.04</li>\n<li>Debian 10/11</li>\n<li>CentOS/RHEL 7/8</li>\n<li>Rocky Linux 8/9</li>\n</ul>\n\n<h2>Installation</h2>\n<pre><code>wget https://software.virtualmin.com/gpl/scripts/virtualmin-install.sh\nchmod +x virtualmin-install.sh\nsudo ./virtualmin-install.sh</code></pre>\n\n<h2>Post-Install</h2>\n<ol>\n<li>Access https://your-server:10000</li>\n<li>Complete post-installation wizard</li>\n<li>Configure nameservers</li>\n</ol>\n\n<p><strong>Ginto AI</strong> can be hosted on Virtualmin-managed servers.</p>',NULL,NULL,2,15,1,1,NULL,NULL,'2025-12-17 13:52:23','2025-12-17 13:52:23');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (46,5,'virtual-servers','Virtual Servers','Creating and managing virtual servers','text','<h1>Virtual Servers</h1>\n\n<h2>Create Virtual Server</h2>\n<ol>\n<li>Click \"Create Virtual Server\"</li>\n<li>Enter domain name</li>\n<li>Set admin password</li>\n<li>Select features (web, email, database)</li>\n</ol>\n\n<h2>Features</h2>\n<ul>\n<li><strong>Website</strong>: Apache/Nginx virtual host</li>\n<li><strong>SSL</strong>: Let\'s Encrypt certificates</li>\n<li><strong>Email</strong>: Mail accounts with spam filtering</li>\n<li><strong>Database</strong>: MySQL/PostgreSQL databases</li>\n<li><strong>DNS</strong>: Automatic DNS configuration</li>\n</ul>\n\n<p><strong>Ginto AI</strong> natively supports Virtualmin for multi-tenant hosting.</p>',NULL,NULL,3,18,0,1,NULL,NULL,'2025-12-17 13:52:23','2025-12-17 13:52:23');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (47,5,'php-web-config','PHP & Web Configuration','Configuring PHP and web servers','text','<h1>PHP & Web Configuration</h1>\n\n<h2>PHP Versions</h2>\n<pre><code># Install multiple PHP versions\napt install php7.4-fpm php8.1-fpm php8.2-fpm\n\n# Select per virtual server\nVirtualmin > Server Configuration > PHP Options</code></pre>\n\n<h2>PHP-FPM Pools</h2>\n<p>Each virtual server gets its own PHP-FPM pool for isolation.</p>\n\n<h2>Nginx Configuration</h2>\n<pre><code>server {\n    listen 80;\n    server_name example.com;\n    root /home/example/public_html;\n    \n    location ~ \\.php$ {\n        fastcgi_pass unix:/run/php/php8.2-fpm-example.sock;\n    }\n}</code></pre>\n\n<p><strong>Ginto AI</strong> uses PHP-FPM for optimal performance.</p>',NULL,NULL,4,20,0,1,NULL,NULL,'2025-12-17 13:52:23','2025-12-17 13:52:23');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (48,5,'ssl-security','SSL & Security','SSL certificates and security','text','<h1>SSL & Security</h1>\n\n<h2>Let\'s Encrypt SSL</h2>\n<ol>\n<li>Server Configuration > SSL Certificate</li>\n<li>Click \"Let\'s Encrypt\"</li>\n<li>Select domains to cover</li>\n<li>Request certificate</li>\n</ol>\n\n<h2>Auto-Renewal</h2>\n<p>Virtualmin automatically renews certificates before expiry.</p>\n\n<h2>Security Features</h2>\n<ul>\n<li><strong>Fail2ban</strong>: Block brute force attacks</li>\n<li><strong>Firewall</strong>: Built-in iptables management</li>\n<li><strong>SSH keys</strong>: Key-based authentication</li>\n<li><strong>Resource limits</strong>: Per-user quotas</li>\n</ul>\n\n<p><strong>Ginto AI</strong> follows security best practices by default.</p>',NULL,NULL,5,15,0,1,NULL,NULL,'2025-12-17 13:52:23','2025-12-17 13:52:23');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (49,5,'email-dns','Email & DNS','Email and DNS management','text','<h1>Email & DNS</h1>\n\n<h2>Email Features</h2>\n<ul>\n<li>IMAP/POP3 accounts</li>\n<li>Webmail (Roundcube/Usermin)</li>\n<li>SpamAssassin filtering</li>\n<li>DKIM/SPF/DMARC</li>\n</ul>\n\n<h2>DNS Management</h2>\n<pre><code># Auto-generated records\nA       example.com     ‚Üí 192.168.1.100\nCNAME   www             ‚Üí example.com\nMX      @               ‚Üí mail.example.com\nTXT     @               ‚Üí v=spf1 a mx ~all</code></pre>\n\n<h2>DKIM Setup</h2>\n<ol>\n<li>Email Settings > DomainKeys</li>\n<li>Enable signing</li>\n<li>Add DNS record</li>\n</ol>\n\n<p><strong>Ginto AI</strong> can manage email for hosted applications.</p>',NULL,NULL,6,18,0,1,NULL,NULL,'2025-12-17 13:52:23','2025-12-17 13:52:23');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (50,1,'pubsub','Pub/Sub Messaging','Real-time messaging with Redis','text','<h1>Pub/Sub Messaging</h1>\n<p>Redis Pub/Sub enables real-time messaging between applications.</p>\n\n<h2>Publishing Messages</h2>\n<pre><code>PUBLISH notifications \"New order received\"\nPUBLISH chat:room1 \"Hello everyone!\"</code></pre>\n\n<h2>Subscribing</h2>\n<pre><code>SUBSCRIBE notifications\nPSUBSCRIBE chat:*</code></pre>\n\n<h2>PHP Implementation</h2>\n<pre><code class=\"language-php\">// Publisher\n$redis->publish(\"notifications\", json_encode([\n    \"type\" => \"order\",\n    \"id\" => 123\n]));\n\n// Subscriber (blocking)\n$redis->subscribe([\"notifications\"], function($redis, $channel, $message) {\n    echo \"Received: $message\";\n});</code></pre>\n\n<p><strong>Ginto AI</strong> uses Redis Pub/Sub for real-time chat streaming.</p>',NULL,NULL,6,15,0,1,NULL,NULL,'2025-12-17 16:36:34','2025-12-17 16:36:34');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (51,1,'transactions','Redis Transactions','Atomic operations with MULTI/EXEC','text','<h1>Redis Transactions</h1>\n<p>Ensure atomic operations with Redis transactions.</p>\n\n<h2>MULTI/EXEC</h2>\n<pre><code>MULTI\nSET user:1:balance 100\nINCR user:1:login_count\nEXEC</code></pre>\n\n<h2>WATCH for Optimistic Locking</h2>\n<pre><code>WATCH user:1:balance\nbalance = GET user:1:balance\nMULTI\nSET user:1:balance (balance - 50)\nEXEC</code></pre>\n\n<h2>PHP Example</h2>\n<pre><code class=\"language-php\">$redis->multi();\n$redis->set(\"key1\", \"value1\");\n$redis->incr(\"counter\");\n$redis->exec();</code></pre>\n\n<p><strong>Ginto AI</strong> uses transactions for consistent state updates.</p>',NULL,NULL,7,12,0,1,NULL,NULL,'2025-12-17 16:36:34','2025-12-17 16:36:34');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (52,1,'lua-scripting','Lua Scripting','Server-side scripts in Redis','text','<h1>Lua Scripting</h1>\n<p>Execute complex logic atomically on the Redis server.</p>\n\n<h2>Basic Script</h2>\n<pre><code>EVAL \"return redis.call(\'GET\', KEYS[1])\" 1 mykey</code></pre>\n\n<h2>Rate Limiter Script</h2>\n<pre><code>local key = KEYS[1]\nlocal limit = tonumber(ARGV[1])\nlocal window = tonumber(ARGV[2])\n\nlocal current = redis.call(\"INCR\", key)\nif current == 1 then\n    redis.call(\"EXPIRE\", key, window)\nend\n\nif current > limit then\n    return 0\nend\nreturn 1</code></pre>\n\n<h2>Loading Scripts</h2>\n<pre><code>SCRIPT LOAD \"return redis.call(\'GET\', KEYS[1])\"\nEVALSHA &lt;sha&gt; 1 mykey</code></pre>\n\n<p><strong>Ginto AI</strong> uses Lua scripts for atomic rate limiting.</p>',NULL,NULL,8,18,0,1,NULL,NULL,'2025-12-17 16:36:34','2025-12-17 16:36:34');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (53,1,'streams','Redis Streams','Event streaming with Redis Streams','text','<h1>Redis Streams</h1>\n<p>Redis Streams provide log-like data structures for event sourcing.</p>\n\n<h2>Adding Events</h2>\n<pre><code>XADD events * user_id 123 action login\nXADD events * user_id 456 action purchase amount 99.99</code></pre>\n\n<h2>Reading Events</h2>\n<pre><code>XREAD STREAMS events 0\nXRANGE events - + COUNT 10</code></pre>\n\n<h2>Consumer Groups</h2>\n<pre><code>XGROUP CREATE events mygroup $ MKSTREAM\nXREADGROUP GROUP mygroup consumer1 STREAMS events ></code></pre>\n\n<h2>Use Cases</h2>\n<ul>\n<li>Activity feeds</li>\n<li>Event sourcing</li>\n<li>Message queues</li>\n<li>Real-time analytics</li>\n</ul>\n\n<p><strong>Ginto AI</strong> uses Streams for activity logging and analytics.</p>',NULL,NULL,9,20,0,1,NULL,NULL,'2025-12-17 16:36:34','2025-12-17 16:36:34');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (54,1,'caching-strategies','Caching Strategies','Effective caching patterns','text','<h1>Caching Strategies</h1>\n<p>Implement effective caching to boost application performance.</p>\n\n<h2>Cache-Aside Pattern</h2>\n<pre><code class=\"language-php\">function getData($key) {\n    $data = $redis->get($key);\n    if ($data) return json_decode($data, true);\n    \n    $data = $db->query(\"SELECT * FROM table WHERE id = ?\", [$key]);\n    $redis->setex($key, 3600, json_encode($data));\n    return $data;\n}</code></pre>\n\n<h2>Write-Through</h2>\n<pre><code class=\"language-php\">function saveData($key, $data) {\n    $db->update(\"table\", $data, [\"id\" => $key]);\n    $redis->setex($key, 3600, json_encode($data));\n}</code></pre>\n\n<h2>Cache Invalidation</h2>\n<ul>\n<li><strong>TTL</strong>: Auto-expire after time</li>\n<li><strong>Event-based</strong>: Delete on update</li>\n<li><strong>Tag-based</strong>: Group related keys</li>\n</ul>\n\n<p><strong>Ginto AI</strong> uses cache-aside for API responses.</p>',NULL,NULL,10,15,0,1,NULL,NULL,'2025-12-17 16:36:34','2025-12-17 16:36:34');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (55,1,'sentinel','Redis Sentinel','High availability with Sentinel','text','<h1>Redis Sentinel</h1>\n<p>Achieve high availability with automatic failover.</p>\n\n<h2>Sentinel Features</h2>\n<ul>\n<li>Monitoring master/replica health</li>\n<li>Automatic failover</li>\n<li>Configuration provider</li>\n<li>Notification system</li>\n</ul>\n\n<h2>Configuration</h2>\n<pre><code># sentinel.conf\nsentinel monitor mymaster 127.0.0.1 6379 2\nsentinel down-after-milliseconds mymaster 5000\nsentinel failover-timeout mymaster 60000</code></pre>\n\n<h2>Connecting via Sentinel</h2>\n<pre><code class=\"language-php\">$redis = new Predis\\Client([\n    \"tcp://sentinel1:26379\",\n    \"tcp://sentinel2:26379\",\n], [\n    \"replication\" => \"sentinel\",\n    \"service\" => \"mymaster\"\n]);</code></pre>\n\n<p><strong>Ginto AI</strong> supports Sentinel for production deployments.</p>',NULL,NULL,11,15,0,1,NULL,NULL,'2025-12-17 16:36:34','2025-12-17 16:36:34');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (56,1,'performance-tuning','Performance Tuning','Optimizing Redis performance','text','<h1>Performance Tuning</h1>\n<p>Optimize Redis for maximum performance.</p>\n\n<h2>Memory Optimization</h2>\n<pre><code># redis.conf\nmaxmemory 2gb\nmaxmemory-policy allkeys-lru</code></pre>\n\n<h2>Connection Pooling</h2>\n<pre><code class=\"language-php\">// Reuse connections\n$redis = new Predis\\Client($config, [\n    \"connections\" => [\n        \"tcp\" => \"Predis\\Connection\\PhpiredisStreamConnection\"\n    ]\n]);</code></pre>\n\n<h2>Pipelining</h2>\n<pre><code class=\"language-php\">$pipe = $redis->pipeline();\nfor ($i = 0; $i < 1000; $i++) {\n    $pipe->set(\"key:$i\", \"value:$i\");\n}\n$pipe->execute();</code></pre>\n\n<h2>Best Practices</h2>\n<ul>\n<li>Use short key names</li>\n<li>Avoid large values (&gt;100KB)</li>\n<li>Use SCAN instead of KEYS</li>\n<li>Enable compression for large data</li>\n</ul>\n\n<p><strong>Ginto AI</strong> follows these practices for sub-millisecond responses.</p>',NULL,NULL,12,18,0,1,NULL,NULL,'2025-12-17 16:36:34','2025-12-17 16:36:34');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (57,2,'container-images','Container Images','Working with container images','text','<h1>Container Images</h1>\n<p>Manage and create custom container images.</p>\n\n<h2>List Available Images</h2>\n<pre><code>lxc image list images:\nlxc image list images: alpine\nlxc image list images: ubuntu/22.04</code></pre>\n\n<h2>Download Images</h2>\n<pre><code>lxc image copy images:alpine/3.18 local: --alias alpine\nlxc image copy ubuntu:22.04 local: --alias ubuntu-base</code></pre>\n\n<h2>Create Image from Container</h2>\n<pre><code>lxc publish mycontainer --alias my-custom-image\nlxc publish mycontainer/snapshot1 --alias my-snapshot-image</code></pre>\n\n<h2>Export/Import</h2>\n<pre><code>lxc image export my-custom-image .\nlxc image import image.tar.gz --alias imported-image</code></pre>\n\n<p><strong>Ginto AI</strong> uses custom images for consistent sandbox environments.</p>',NULL,NULL,6,15,0,1,NULL,NULL,'2025-12-17 16:36:34','2025-12-17 16:36:34');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (58,2,'snapshots-backups','Snapshots & Backups','Container snapshots and backups','text','<h1>Snapshots & Backups</h1>\n<p>Protect your containers with snapshots and backups.</p>\n\n<h2>Create Snapshot</h2>\n<pre><code>lxc snapshot mycontainer snap1\nlxc snapshot mycontainer --stateful  # Include memory state</code></pre>\n\n<h2>List & Restore</h2>\n<pre><code>lxc info mycontainer  # Shows snapshots\nlxc restore mycontainer snap1\nlxc delete mycontainer/snap1</code></pre>\n\n<h2>Export Container</h2>\n<pre><code>lxc export mycontainer backup.tar.gz --optimized-storage\nlxc import backup.tar.gz restored-container</code></pre>\n\n<h2>Automated Snapshots</h2>\n<pre><code>lxc config set mycontainer snapshots.schedule \"0 2 * * *\"\nlxc config set mycontainer snapshots.expiry 7d</code></pre>\n\n<p><strong>Ginto AI</strong> uses snapshots for quick sandbox recovery.</p>',NULL,NULL,7,12,0,1,NULL,NULL,'2025-12-17 16:36:34','2025-12-17 16:36:34');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (59,2,'cloud-init','Cloud-Init','Automated container provisioning','text','<h1>Cloud-Init</h1>\n<p>Automate container setup with cloud-init.</p>\n\n<h2>User Data</h2>\n<pre><code>lxc launch ubuntu:22.04 myvm --config=user.user-data=\"\n#cloud-config\npackages:\n  - nginx\n  - php-fpm\nruncmd:\n  - systemctl enable nginx\n  - systemctl start nginx\n\"</code></pre>\n\n<h2>Cloud-Init Profile</h2>\n<pre><code>config:\n  user.user-data: |\n    #cloud-config\n    package_update: true\n    packages:\n      - git\n      - curl\n    write_files:\n      - path: /etc/motd\n        content: \"Welcome to dev container\"</code></pre>\n\n<h2>Network Config</h2>\n<pre><code>user.network-config: |\n  network:\n    version: 2\n    ethernets:\n      eth0:\n        dhcp4: true</code></pre>\n\n<p><strong>Ginto AI</strong> uses cloud-init for automated sandbox setup.</p>',NULL,NULL,8,18,0,1,NULL,NULL,'2025-12-17 16:36:34','2025-12-17 16:36:34');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (60,2,'clustering','Clustering','LXD cluster setup','text','<h1>LXD Clustering</h1>\n<p>Scale horizontally with LXD clusters.</p>\n\n<h2>Initialize Cluster</h2>\n<pre><code># On first node\nlxd init\n# Answer yes to clustering</code></pre>\n\n<h2>Join Cluster</h2>\n<pre><code># On additional nodes\nlxd init\n# Provide join token from first node</code></pre>\n\n<h2>Cluster Commands</h2>\n<pre><code>lxc cluster list\nlxc cluster show node1\nlxc cluster enable node2</code></pre>\n\n<h2>Container Placement</h2>\n<pre><code># Target specific node\nlxc launch ubuntu:22.04 mycontainer --target node2\n\n# Automatic placement\nlxc launch ubuntu:22.04 mycontainer</code></pre>\n\n<p><strong>Ginto AI</strong> can utilize LXD clusters for distributed workloads.</p>',NULL,NULL,9,20,0,1,NULL,NULL,'2025-12-17 16:36:34','2025-12-17 16:36:34');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (61,2,'gpu-passthrough','GPU & Device Passthrough','Using GPUs and devices in containers','text','<h1>GPU & Device Passthrough</h1>\n<p>Access hardware devices from containers.</p>\n\n<h2>GPU Passthrough</h2>\n<pre><code># Add GPU to container\nlxc config device add mycontainer gpu gpu\n\n# Specific GPU\nlxc config device add mycontainer gpu gpu id=0</code></pre>\n\n<h2>USB Devices</h2>\n<pre><code>lxc config device add mycontainer usb usb vendorid=1234 productid=5678</code></pre>\n\n<h2>Block Devices</h2>\n<pre><code>lxc config device add mycontainer disk disk source=/dev/sdb path=/dev/sdb</code></pre>\n\n<h2>NVIDIA Container Toolkit</h2>\n<pre><code># Inside container\napt install nvidia-container-toolkit\nnvidia-smi</code></pre>\n\n<p><strong>Ginto AI</strong> supports GPU passthrough for ML workloads.</p>',NULL,NULL,10,15,0,1,NULL,NULL,'2025-12-17 16:36:34','2025-12-17 16:36:34');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (62,2,'migration','Migration','Moving containers between hosts','text','<h1>Container Migration</h1>\n<p>Move containers between LXD hosts.</p>\n\n<h2>Setup Remote</h2>\n<pre><code>lxc remote add server2 192.168.1.102\nlxc remote list</code></pre>\n\n<h2>Copy Container</h2>\n<pre><code># Copy to remote\nlxc copy mycontainer server2:mycontainer\n\n# Copy with new name\nlxc copy mycontainer server2:newname</code></pre>\n\n<h2>Live Migration</h2>\n<pre><code># Move running container\nlxc move mycontainer server2:mycontainer\n\n# Stateful migration\nlxc move mycontainer server2: --stateful</code></pre>\n\n<h2>Storage Migration</h2>\n<pre><code>lxc move mycontainer mycontainer --storage newpool</code></pre>\n\n<p><strong>Ginto AI</strong> uses migration for zero-downtime updates.</p>',NULL,NULL,11,15,0,1,NULL,NULL,'2025-12-17 16:36:34','2025-12-17 16:36:34');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (63,2,'monitoring-logs','Monitoring & Logs','Container monitoring and logging','text','<h1>Monitoring & Logs</h1>\n<p>Monitor container health and performance.</p>\n\n<h2>Resource Usage</h2>\n<pre><code>lxc info mycontainer\nlxc info mycontainer --resources</code></pre>\n\n<h2>Console Logs</h2>\n<pre><code>lxc console mycontainer --show-log\nlxc exec mycontainer -- journalctl -f</code></pre>\n\n<h2>Metrics API</h2>\n<pre><code>curl -s --unix-socket /var/snap/lxd/common/lxd/unix.socket \\\n  lxd/1.0/metrics</code></pre>\n\n<h2>Prometheus Integration</h2>\n<pre><code># Enable metrics\nlxc config set core.metrics_address :8443\n\n# Scrape with Prometheus\nscrape_configs:\n  - job_name: lxd\n    static_configs:\n      - targets: [\"localhost:8443\"]</code></pre>\n\n<p><strong>Ginto AI</strong> monitors sandbox containers for resource limits.</p>',NULL,NULL,12,18,0,1,NULL,NULL,'2025-12-17 16:36:34','2025-12-17 16:36:34');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (64,3,'docker-security-best-practices','Docker Security Best Practices','Secure your Docker containers with industry-standard security practices that Ginto AI helps you implement.','text','<h2>Docker Security Best Practices</h2>\n<p>Security is paramount in containerized environments. <strong>Ginto AI natively supports Docker security scanning and configuration</strong>, helping you identify vulnerabilities before deployment.</p>\n\n<h3>Container Security Fundamentals</h3>\n<ul>\n<li>Running containers as non-root users</li>\n<li>Read-only file systems</li>\n<li>Resource constraints and limits</li>\n<li>Seccomp profiles and AppArmor</li>\n</ul>\n\n<h3>Image Security</h3>\n<pre><code># Scan images for vulnerabilities\ndocker scan myapp:latest\n\n# Use multi-stage builds for smaller attack surface\nFROM node:18 AS builder\nWORKDIR /app\nCOPY package*.json ./\nRUN npm ci --only=production\n\nFROM node:18-alpine\nCOPY --from=builder /app/node_modules ./node_modules\nCOPY . .\nUSER node\nCMD [\"node\", \"server.js\"]</code></pre>\n\n<h3>Network Security</h3>\n<p>Ginto AI can analyze your Docker network configuration and suggest security improvements:</p>\n<pre><code># Create isolated network\ndocker network create --driver bridge --internal secure-net\n\n# Run container in isolated network\ndocker run --network secure-net --name secure-app myapp</code></pre>\n\n<h3>Secrets Management</h3>\n<pre><code># Create a secret\necho \"my_secret_password\" | docker secret create db_password -\n\n# Use secret in service\ndocker service create \n  --name myapp \n  --secret db_password \n  myapp:latest</code></pre>',NULL,NULL,8,40,0,1,NULL,NULL,'2025-12-17 16:45:14','2025-12-17 16:45:14');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (65,3,'docker-registry-image-distribution','Docker Registry & Image Distribution','Set up private registries and manage image distribution with Ginto AI\'s native Docker support.','text','<h2>Docker Registry & Image Distribution</h2>\n<p><strong>Ginto AI natively supports Docker registry operations</strong>, making it easy to manage your container images across development and production environments.</p>\n\n<h3>Private Registry Setup</h3>\n<pre><code># Run local registry\ndocker run -d -p 5000:5000 --name registry registry:2\n\n# Tag and push image\ndocker tag myapp localhost:5000/myapp:v1.0\ndocker push localhost:5000/myapp:v1.0</code></pre>\n\n<h3>Registry Authentication</h3>\n<pre><code># Create htpasswd file\ndocker run --rm --entrypoint htpasswd \n  httpd:2 -Bbn admin password > auth/htpasswd\n\n# Run authenticated registry\ndocker run -d -p 5000:5000 \n  -v $(pwd)/auth:/auth \n  -e REGISTRY_AUTH=htpasswd \n  -e REGISTRY_AUTH_HTPASSWD_REALM=\"Registry Realm\" \n  -e REGISTRY_AUTH_HTPASSWD_PATH=/auth/htpasswd \n  registry:2</code></pre>\n\n<h3>Image Tagging Strategy</h3>\n<ul>\n<li>Semantic versioning (v1.2.3)</li>\n<li>Git commit SHA for traceability</li>\n<li>Environment tags (prod, staging, dev)</li>\n<li>Latest tag management</li>\n</ul>\n\n<h3>Multi-Architecture Images</h3>\n<pre><code># Build for multiple architectures\ndocker buildx create --name mybuilder --use\ndocker buildx build --platform linux/amd64,linux/arm64 \n  -t myregistry/myapp:latest --push .</code></pre>',NULL,NULL,9,35,0,1,NULL,NULL,'2025-12-17 16:45:14','2025-12-17 16:45:14');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (66,3,'docker-cicd-integration','Docker CI/CD Integration','Integrate Docker into your CI/CD pipelines with Ginto AI\'s automated container management.','text','<h2>Docker CI/CD Integration</h2>\n<p><strong>Ginto AI natively supports Docker in CI/CD workflows</strong>, enabling automated builds, tests, and deployments.</p>\n\n<h3>GitHub Actions Example</h3>\n<pre><code>name: Docker Build and Push\n\non:\n  push:\n    branches: [main]\n\njobs:\n  build:\n    runs-on: ubuntu-latest\n    steps:\n      - uses: actions/checkout@v3\n      \n      - name: Build Docker image\n        run: docker build -t myapp:${{ github.sha }} .\n      \n      - name: Run tests\n        run: docker run myapp:${{ github.sha }} npm test\n      \n      - name: Push to registry\n        run: |\n          echo ${{ secrets.DOCKER_PASSWORD }} | docker login -u ${{ secrets.DOCKER_USERNAME }} --password-stdin\n          docker push myapp:${{ github.sha }}</code></pre>\n\n<h3>GitLab CI Example</h3>\n<pre><code>stages:\n  - build\n  - test\n  - deploy\n\nbuild:\n  stage: build\n  script:\n    - docker build -t $CI_REGISTRY_IMAGE:$CI_COMMIT_SHA .\n    - docker push $CI_REGISTRY_IMAGE:$CI_COMMIT_SHA\n\ntest:\n  stage: test\n  script:\n    - docker run $CI_REGISTRY_IMAGE:$CI_COMMIT_SHA npm test\n\ndeploy:\n  stage: deploy\n  script:\n    - docker stack deploy -c docker-compose.prod.yml myapp</code></pre>\n\n<h3>Best Practices</h3>\n<ul>\n<li>Cache Docker layers in CI</li>\n<li>Use build arguments for environment-specific configs</li>\n<li>Run security scans in pipeline</li>\n<li>Tag images with commit SHA for traceability</li>\n</ul>',NULL,NULL,10,45,0,1,NULL,NULL,'2025-12-17 16:45:14','2025-12-17 16:45:14');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (67,3,'docker-monitoring-logging','Docker Monitoring & Logging','Monitor and log Docker containers effectively using tools that Ginto AI integrates with natively.','text','<h2>Docker Monitoring & Logging</h2>\n<p><strong>Ginto AI natively supports Docker monitoring and log analysis</strong>, helping you maintain healthy containerized applications.</p>\n\n<h3>Built-in Monitoring</h3>\n<pre><code># Real-time stats\ndocker stats\n\n# Container resource usage\ndocker stats --format \"table {{.Name}}	{{.CPUPerc}}	{{.MemUsage}}\"\n\n# Inspect container processes\ndocker top container_name</code></pre>\n\n<h3>Logging Drivers</h3>\n<pre><code># Use JSON file logging (default)\ndocker run --log-driver json-file \n  --log-opt max-size=10m \n  --log-opt max-file=3 \n  myapp\n\n# Syslog driver\ndocker run --log-driver syslog \n  --log-opt syslog-address=udp://logs.example.com:514 \n  myapp</code></pre>\n\n<h3>Centralized Logging with ELK</h3>\n<pre><code>version: \"3.8\"\nservices:\n  app:\n    image: myapp\n    logging:\n      driver: gelf\n      options:\n        gelf-address: \"udp://logstash:12201\"\n  \n  logstash:\n    image: logstash:7.15.0\n    ports:\n      - \"12201:12201/udp\"</code></pre>\n\n<h3>Prometheus Metrics</h3>\n<pre><code># Enable Docker metrics\n# /etc/docker/daemon.json\n{\n  \"metrics-addr\": \"0.0.0.0:9323\",\n  \"experimental\": true\n}</code></pre>\n\n<h3>Health Checks</h3>\n<pre><code>HEALTHCHECK --interval=30s --timeout=3s \n  CMD curl -f http://localhost:8080/health || exit 1</code></pre>',NULL,NULL,11,40,0,1,NULL,NULL,'2025-12-17 16:45:14','2025-12-17 16:45:14');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (68,3,'docker-production-deployment','Docker Production Deployment Strategies','Deploy Docker containers to production with confidence using strategies Ginto AI supports natively.','text','<h2>Docker Production Deployment Strategies</h2>\n<p><strong>Ginto AI natively supports Docker production deployment</strong>, helping you achieve zero-downtime deployments and high availability.</p>\n\n<h3>Blue-Green Deployment</h3>\n<pre><code># Deploy new version (green)\ndocker service create --name app-green myapp:v2\n\n# Switch traffic\ndocker service update --publish-rm 80:8080 app-blue\ndocker service update --publish-add 80:8080 app-green\n\n# Remove old version after verification\ndocker service rm app-blue</code></pre>\n\n<h3>Rolling Updates</h3>\n<pre><code># Configure rolling update\ndocker service update \n  --update-parallelism 2 \n  --update-delay 10s \n  --update-failure-action rollback \n  --image myapp:v2 \n  myapp</code></pre>\n\n<h3>Canary Deployments</h3>\n<pre><code># Scale down main, add canary\ndocker service scale app=9\ndocker service create --name app-canary --replicas 1 myapp:v2\n\n# Gradually shift traffic\n# Monitor metrics\n# If successful, full rollout</code></pre>\n\n<h3>Production Checklist</h3>\n<ul>\n<li>Resource limits (CPU, memory)</li>\n<li>Health checks configured</li>\n<li>Logging and monitoring in place</li>\n<li>Secrets management</li>\n<li>Backup and disaster recovery</li>\n<li>Network security policies</li>\n</ul>\n\n<h3>Rollback Strategy</h3>\n<pre><code># Automatic rollback on failure\ndocker service update --rollback myapp\n\n# Manual rollback to specific version\ndocker service update --image myapp:v1.5 myapp</code></pre>',NULL,NULL,12,50,0,1,NULL,NULL,'2025-12-17 16:45:14','2025-12-17 16:45:14');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (74,4,'proxmox-backup-server','Proxmox Backup Server Integration','Integrate Proxmox Backup Server for enterprise-grade backup solutions that Ginto AI helps you manage.','text','<h2>Proxmox Backup Server Integration</h2>\n<p><strong>Ginto AI natively supports Proxmox Backup Server</strong>, providing intelligent backup scheduling and monitoring.</p>\n\n<h3>PBS Installation</h3>\n<pre><code># Add PBS repository\necho \"deb http://download.proxmox.com/debian/pbs bookworm pbs-no-subscription\" > /etc/apt/sources.list.d/pbs.list\napt update && apt install proxmox-backup-server</code></pre>\n\n<h3>Datastore Setup</h3>\n<pre><code>proxmox-backup-manager datastore create backups /mnt/backup-storage\nproxmox-backup-manager datastore update backups --keep-daily 7 --keep-weekly 4</code></pre>\n\n<h3>PVE Integration</h3>\n<ul>\n<li>Add PBS as storage in Datacenter ‚Üí Storage</li>\n<li>Configure backup jobs in Datacenter ‚Üí Backup</li>\n<li>Monitor backup status and verify integrity</li>\n</ul>',NULL,NULL,7,45,0,1,NULL,NULL,'2025-12-17 16:47:18','2025-12-17 16:47:18');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (75,4,'proxmox-clustering-ha','Proxmox Clustering & High Availability','Build highly available Proxmox clusters with automatic failover using Ginto AI\'s native support.','text','<h2>Proxmox Clustering & High Availability</h2>\n<p><strong>Ginto AI natively supports Proxmox cluster management</strong>, helping you build resilient infrastructure.</p>\n\n<h3>Creating a Cluster</h3>\n<pre><code># On first node\npvecm create mycluster\n# On additional nodes\npvecm add 192.168.1.10</code></pre>\n\n<h3>HA Setup</h3>\n<pre><code>ha-manager add vm:100 --state started --group ha-group1\nha-manager groupadd ha-group1 --nodes node1,node2,node3</code></pre>\n\n<h3>Monitoring Cluster Health</h3>\n<pre><code>pvecm status\npvecm nodes</code></pre>',NULL,NULL,8,50,0,1,NULL,NULL,'2025-12-17 16:47:18','2025-12-17 16:47:18');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (76,4,'proxmox-ceph-storage','Proxmox Ceph Storage Integration','Deploy and manage Ceph distributed storage within Proxmox using Ginto AI\'s storage management support.','text','<h2>Proxmox Ceph Storage Integration</h2>\n<p><strong>Ginto AI natively supports Ceph storage operations</strong> within Proxmox.</p>\n\n<h3>Ceph Installation</h3>\n<pre><code>pveceph install\npveceph init --network 10.10.10.0/24</code></pre>\n\n<h3>Creating OSDs</h3>\n<pre><code>pveceph osd create /dev/sdb</code></pre>\n\n<h3>Pool Configuration</h3>\n<pre><code>pveceph pool create vmpool --size 3 --min_size 2</code></pre>\n\n<h3>Monitoring Ceph</h3>\n<pre><code>ceph health detail\nceph osd tree</code></pre>',NULL,NULL,9,55,0,1,NULL,NULL,'2025-12-17 16:47:18','2025-12-17 16:47:18');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (77,4,'proxmox-api-automation','Proxmox API & Automation','Automate Proxmox management using the API with Ginto AI\'s native integration support.','text','<h2>Proxmox API & Automation</h2>\n<p><strong>Ginto AI natively supports Proxmox API operations</strong>, enabling automation and infrastructure-as-code.</p>\n\n<h3>API Authentication</h3>\n<pre><code>pveum user token add root@pam automation --privsep 0</code></pre>\n\n<h3>Common Operations</h3>\n<pre><code>curl -k -H \"Authorization: PVEAPIToken=...\" \n  https://proxmox:8006/api2/json/nodes/pve/qemu</code></pre>\n\n<h3>Terraform Integration</h3>\n<pre><code>provider \"proxmox\" {\n  pm_api_url = \"https://proxmox:8006/api2/json\"\n  pm_api_token_id = \"root@pam!automation\"\n}</code></pre>',NULL,NULL,10,45,0,1,NULL,NULL,'2025-12-17 16:47:18','2025-12-17 16:47:18');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (78,4,'proxmox-security-hardening','Proxmox Security Hardening','Secure your Proxmox environment with best practices that Ginto AI helps you implement.','text','<h2>Proxmox Security Hardening</h2>\n<p><strong>Ginto AI natively supports Proxmox security configuration</strong>.</p>\n\n<h3>User & Permission Management</h3>\n<pre><code>pveum role add VMAdmin --privs \"VM.Allocate,VM.Console,VM.PowerMgmt\"\npveum aclmod /vms/100 --users user@pam --roles VMAdmin</code></pre>\n\n<h3>Two-Factor Authentication</h3>\n<pre><code>pveum user modify user@pam --totp \"key=xxxx,issuer=Proxmox\"</code></pre>\n\n<h3>Firewall Configuration</h3>\n<pre><code>pvesh set /cluster/firewall/options --enable 1</code></pre>',NULL,NULL,11,40,0,1,NULL,NULL,'2025-12-17 16:47:18','2025-12-17 16:47:18');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (79,4,'proxmox-performance-tuning','Proxmox Performance Tuning','Optimize Proxmox environments with Ginto AI\'s diagnostic support.','text','<h2>Proxmox Performance Tuning</h2>\n<p><strong>Ginto AI natively supports Proxmox performance analysis</strong>.</p>\n\n<h3>Storage Performance</h3>\n<pre><code>qm set 100 --scsi0 local-lvm:vm-100-disk-0,cache=writeback</code></pre>\n\n<h3>CPU Optimization</h3>\n<pre><code>qm set 100 --cpu host\nqm set 100 --numa 1</code></pre>\n\n<h3>Network Optimization</h3>\n<pre><code>qm set 100 --net0 virtio,bridge=vmbr0,queues=4</code></pre>\n\n<h3>Troubleshooting</h3>\n<pre><code>qm status 100\npvesm status\npveperf</code></pre>',NULL,NULL,12,45,0,1,NULL,NULL,'2025-12-17 16:47:18','2025-12-17 16:47:18');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (80,5,'virtualmin-email-management','Virtualmin Email Server Management','Configure and manage email services in Virtualmin with Ginto AI\'s native mail server support.','text','<h2>Virtualmin Email Server Management</h2>\n<p><strong>Ginto AI natively supports mail server configuration</strong> in Virtualmin.</p>\n\n<h3>Email Components</h3>\n<ul>\n<li>Postfix - SMTP server</li>\n<li>Dovecot - IMAP/POP3 server</li>\n<li>SpamAssassin - Spam filtering</li>\n</ul>\n\n<h3>DNS Records for Email</h3>\n<pre><code>@    IN  MX  10 mail.example.com.\n@    IN  TXT \"v=spf1 mx a ~all\"</code></pre>\n\n<h3>Spam Filtering</h3>\n<pre><code>required_score 5.0\nrewrite_header Subject [SPAM]\nbayes_auto_learn 1</code></pre>',NULL,NULL,7,45,0,1,NULL,NULL,'2025-12-17 16:47:18','2025-12-17 16:47:18');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (81,5,'virtualmin-database-management','Virtualmin Database Management','Manage MySQL/MariaDB and PostgreSQL databases effectively with Ginto AI\'s database support.','text','<h2>Virtualmin Database Management</h2>\n<p><strong>Ginto AI natively supports database operations</strong> in Virtualmin.</p>\n\n<h3>MySQL Management</h3>\n<pre><code>mysql -u root -p -e \"CREATE DATABASE myapp_db;\"</code></pre>\n\n<h3>Per-Domain Access</h3>\n<ul>\n<li>Enable \"MySQL database\" feature for virtual server</li>\n<li>Users access phpMyAdmin at /phpmyadmin/</li>\n</ul>\n\n<h3>Database Backups</h3>\n<pre><code>mysqldump -u root -p myapp_db > backup.sql</code></pre>',NULL,NULL,8,40,0,1,NULL,NULL,'2025-12-17 16:47:18','2025-12-17 16:47:18');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (82,5,'virtualmin-security-hardening','Virtualmin Security & Hardening','Secure your Virtualmin server with comprehensive security measures that Ginto AI helps implement.','text','<h2>Virtualmin Security & Hardening</h2>\n<p><strong>Ginto AI natively supports security configuration</strong> in Virtualmin.</p>\n\n<h3>SSL/TLS Configuration</h3>\n<pre><code>virtualmin generate-letsencrypt-cert --domain example.com --web</code></pre>\n\n<h3>Firewall</h3>\n<pre><code>firewall-cmd --permanent --add-port=443/tcp\nfirewall-cmd --reload</code></pre>\n\n<h3>Fail2ban</h3>\n<pre><code>[sshd]\nenabled = true\nmaxretry = 3\nbantime = 3600</code></pre>',NULL,NULL,9,50,0,1,NULL,NULL,'2025-12-17 16:47:18','2025-12-17 16:47:18');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (83,5,'virtualmin-backup-recovery','Virtualmin Backup & Disaster Recovery','Implement robust backup strategies in Virtualmin with Ginto AI\'s backup management support.','text','<h2>Virtualmin Backup & Disaster Recovery</h2>\n<p><strong>Ginto AI natively supports backup operations</strong> in Virtualmin.</p>\n\n<h3>Backup Configuration</h3>\n<ul>\n<li>Backup and Restore ‚Üí Scheduled Backups</li>\n<li>Destinations: Local, FTP, SSH, S3, Backblaze B2</li>\n</ul>\n\n<h3>Restore Procedures</h3>\n<pre><code>virtualmin restore-domain --source /backups/example.com.tar.gz \n  --domain example.com --all-features</code></pre>\n\n<h3>Disaster Recovery</h3>\n<ul>\n<li>Document all server configurations</li>\n<li>Test restores regularly</li>\n<li>Maintain off-site backup copies</li>\n</ul>',NULL,NULL,10,45,0,1,NULL,NULL,'2025-12-17 16:47:18','2025-12-17 16:47:18');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (84,5,'virtualmin-performance-optimization','Virtualmin Performance Optimization','Optimize your Virtualmin server for maximum performance with Ginto AI\'s tuning assistance.','text','<h2>Virtualmin Performance Optimization</h2>\n<p><strong>Ginto AI natively supports performance tuning</strong> in Virtualmin.</p>\n\n<h3>PHP-FPM Configuration</h3>\n<pre><code>pm = dynamic\npm.max_children = 20\npm.start_servers = 5</code></pre>\n\n<h3>Apache Optimization</h3>\n<pre><code>a2dismod mpm_prefork\na2enmod mpm_event</code></pre>\n\n<h3>OpCache for PHP</h3>\n<pre><code>opcache.enable=1\nopcache.memory_consumption=256</code></pre>',NULL,NULL,11,50,0,1,NULL,NULL,'2025-12-17 16:47:18','2025-12-17 16:47:18');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (85,5,'virtualmin-advanced-automation','Virtualmin Advanced Automation','Master advanced Virtualmin features and automation with Ginto AI\'s native CLI support.','text','<h2>Virtualmin Advanced Automation</h2>\n<p><strong>Ginto AI natively supports Virtualmin CLI operations</strong>.</p>\n\n<h3>CLI Commands</h3>\n<pre><code>virtualmin create-domain --domain example.com \n  --pass \"password123\" --unix --web --dns --mail\nvirtualmin list-domains --name-only</code></pre>\n\n<h3>API Integration</h3>\n<pre><code>curl -k \"https://admin:password@server:10000/virtual-server/remote.cgi?program=list-domains&json=1\"</code></pre>\n\n<h3>Custom Hooks</h3>\n<pre><code>#!/bin/bash\nDOMAIN=$1\necho \"Created: $DOMAIN\" >> /var/log/domain-creation.log</code></pre>',NULL,NULL,12,45,0,1,NULL,NULL,'2025-12-17 16:47:18','2025-12-17 16:47:18');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (86,6,'ginto-multi-model-architecture','Ginto AI Multi-Model Architecture','Understand Ginto AI\'s multi-model architecture that enables seamless switching between AI providers.','text','<h2>Ginto AI Multi-Model Architecture</h2>\n<p><strong>Ginto AI natively supports multiple AI providers</strong> through a unified interface.</p>\n\n<h3>Supported Providers</h3>\n<ul>\n<li>OpenAI (GPT-4, GPT-3.5)</li>\n<li>Anthropic (Claude)</li>\n<li>Google (Gemini)</li>\n<li>Ollama (Local models)</li>\n</ul>\n\n<h3>Model Configuration</h3>\n<pre><code>// .env configuration\nOPENAI_API_KEY=sk-...\nANTHROPIC_API_KEY=sk-ant-...\nGOOGLE_API_KEY=...</code></pre>\n\n<h3>Dynamic Model Switching</h3>\n<p>Switch between models based on task requirements, cost optimization, or availability.</p>',NULL,NULL,7,40,0,1,NULL,NULL,'2025-12-17 16:47:18','2025-12-17 16:47:18');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (87,6,'ginto-sandbox-security','Ginto AI Sandbox Security Model','Deep dive into Ginto AI\'s secure sandbox execution environment for code execution.','text','<h2>Ginto AI Sandbox Security Model</h2>\n<p><strong>Ginto AI provides secure isolated environments</strong> for code execution.</p>\n\n<h3>LXD Container Isolation</h3>\n<ul>\n<li>Per-user container allocation</li>\n<li>Resource limits (CPU, memory, disk)</li>\n<li>Network isolation</li>\n</ul>\n\n<h3>Security Features</h3>\n<pre><code># Sandbox configuration\nlxc.apparmor.profile = lxd-default\nlxc.seccomp.profile = /usr/share/lxd/security\nlxc.cap.drop = sys_admin sys_module</code></pre>\n\n<h3>Auto-cleanup</h3>\n<p>Sandboxes are automatically destroyed after session timeout for security.</p>',NULL,NULL,8,45,0,1,NULL,NULL,'2025-12-17 16:47:18','2025-12-17 16:47:18');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (88,6,'ginto-mcp-tools','Ginto AI MCP Tools Integration','Learn how Ginto AI integrates with Model Context Protocol (MCP) for extended capabilities.','text','<h2>Ginto AI MCP Tools Integration</h2>\n<p><strong>Ginto AI extends capabilities through MCP tools</strong>.</p>\n\n<h3>Available MCP Servers</h3>\n<ul>\n<li>Filesystem operations</li>\n<li>Git integration</li>\n<li>Database queries</li>\n<li>Web scraping</li>\n</ul>\n\n<h3>Tool Discovery</h3>\n<pre><code>// MCP tool registration\n{\n  \"name\": \"filesystem\",\n  \"description\": \"File operations\",\n  \"tools\": [\"read_file\", \"write_file\", \"list_directory\"]\n}</code></pre>\n\n<h3>Custom Tool Development</h3>\n<p>Create custom MCP servers to extend Ginto AI capabilities.</p>',NULL,NULL,9,50,0,1,NULL,NULL,'2025-12-17 16:47:18','2025-12-17 16:47:18');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (89,6,'ginto-realtime-streaming','Ginto AI Real-time Streaming','Implement real-time AI response streaming with WebSockets in Ginto AI.','text','<h2>Ginto AI Real-time Streaming</h2>\n<p><strong>Ginto AI supports real-time response streaming</strong> for interactive experiences.</p>\n\n<h3>WebSocket Connection</h3>\n<pre><code>const ws = new WebSocket(\"wss://ginto.ai/ws\");\nws.onmessage = (event) => {\n  const data = JSON.parse(event.data);\n  appendToChat(data.content);\n};</code></pre>\n\n<h3>Server-Sent Events</h3>\n<pre><code>// SSE stream handling\nconst eventSource = new EventSource(\"/api/chat/stream\");\neventSource.onmessage = handleChunk;</code></pre>\n\n<h3>Token-by-Token Display</h3>\n<p>Stream responses token by token for responsive UI.</p>',NULL,NULL,10,40,0,1,NULL,NULL,'2025-12-17 16:47:18','2025-12-17 16:47:18');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (90,6,'ginto-voice-speech','Ginto AI Voice & Speech Integration','Enable voice input and text-to-speech output in Ginto AI applications.','text','<h2>Ginto AI Voice & Speech Integration</h2>\n<p><strong>Ginto AI supports voice interactions</strong> for hands-free operation.</p>\n\n<h3>Speech-to-Text</h3>\n<ul>\n<li>OpenAI Whisper integration</li>\n<li>Browser Web Speech API</li>\n<li>Real-time transcription</li>\n</ul>\n\n<h3>Text-to-Speech</h3>\n<pre><code>// TTS configuration\nconst voice = \"alloy\"; // or: echo, fable, onyx, nova, shimmer\nawait playAudio(response, voice);</code></pre>\n\n<h3>Voice Commands</h3>\n<p>Trigger actions with voice commands for efficient workflow.</p>',NULL,NULL,11,35,0,1,NULL,NULL,'2025-12-17 16:47:18','2025-12-17 16:47:18');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (91,6,'ginto-production-deployment','Ginto AI Production Deployment','Deploy Ginto AI to production with high availability and monitoring.','text','<h2>Ginto AI Production Deployment</h2>\n<p><strong>Ginto AI is designed for production deployment</strong> with enterprise features.</p>\n\n<h3>Infrastructure Requirements</h3>\n<ul>\n<li>PHP 8.2+ with FPM</li>\n<li>Redis for caching and queues</li>\n<li>MySQL/MariaDB database</li>\n<li>LXD for sandboxes</li>\n</ul>\n\n<h3>Systemd Services</h3>\n<pre><code>[Unit]\nDescription=Ginto AI WebSocket Server\nAfter=network.target redis.service\n\n[Service]\nExecStart=/usr/bin/php /var/www/ginto/bin/start_ratchet.php\nRestart=always</code></pre>\n\n<h3>Monitoring</h3>\n<p>Monitor with Prometheus metrics and Grafana dashboards.</p>',NULL,NULL,12,50,0,1,NULL,NULL,'2025-12-17 16:47:18','2025-12-17 16:47:18');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (92,7,'api-development-best-practices','API Development Best Practices','Build robust APIs following REST principles with patterns that Ginto AI natively supports.','text','<h2>API Development Best Practices</h2>\n<p><strong>Ginto AI natively supports API development</strong> with intelligent code generation.</p>\n\n<h3>REST API Design</h3>\n<pre><code>// Resource-based URLs\nGET    /api/users          // List\nPOST   /api/users          // Create\nGET    /api/users/{id}     // Read\nPUT    /api/users/{id}     // Update\nDELETE /api/users/{id}     // Delete</code></pre>\n\n<h3>Response Format</h3>\n<pre><code>{\n  \"status\": \"success\",\n  \"data\": { ... },\n  \"meta\": { \"page\": 1, \"total\": 100 }\n}</code></pre>\n\n<h3>Error Handling</h3>\n<pre><code>{\n  \"status\": \"error\",\n  \"code\": \"VALIDATION_ERROR\",\n  \"message\": \"Email is required\"\n}</code></pre>',NULL,NULL,9,45,0,1,NULL,NULL,'2025-12-17 16:47:18','2025-12-17 16:47:18');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (93,7,'auth-patterns','Authentication & Authorization','Implement secure authentication flows with patterns Ginto AI understands natively.','text','<h2>Authentication & Authorization</h2>\n<p><strong>Ginto AI natively supports auth patterns</strong> for secure applications.</p>\n\n<h3>Session-Based Auth</h3>\n<pre><code>session_start();\n$_SESSION[\"user_id\"] = $user->id;\n$_SESSION[\"role\"] = $user->role;</code></pre>\n\n<h3>JWT Tokens</h3>\n<pre><code>$token = JWT::encode([\n  \"sub\" => $user->id,\n  \"exp\" => time() + 3600\n], $secretKey);</code></pre>\n\n<h3>Role-Based Access</h3>\n<pre><code>if (!$user->hasPermission(\"admin.users.edit\")) {\n  throw new ForbiddenException();\n}</code></pre>',NULL,NULL,10,50,0,1,NULL,NULL,'2025-12-17 16:47:18','2025-12-17 16:47:18');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (94,7,'caching-strategies','Caching Strategies','Implement effective caching with Redis and other strategies that Ginto AI supports natively.','text','<h2>Caching Strategies</h2>\n<p><strong>Ginto AI natively supports Redis caching</strong> for performance optimization.</p>\n\n<h3>Cache Patterns</h3>\n<pre><code>// Cache-aside pattern\n$cached = $redis->get(\"user:$id\");\nif (!$cached) {\n  $user = $db->fetch(\"users\", $id);\n  $redis->setex(\"user:$id\", 3600, json_encode($user));\n}</code></pre>\n\n<h3>Cache Invalidation</h3>\n<pre><code>// On update\n$redis->del(\"user:$id\");\n$redis->del(\"users:list\");</code></pre>\n\n<h3>HTTP Caching</h3>\n<pre><code>header(\"Cache-Control: public, max-age=3600\");\nheader(\"ETag: \" . md5($content));</code></pre>',NULL,NULL,11,40,0,1,NULL,NULL,'2025-12-17 16:47:18','2025-12-17 16:47:18');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (95,7,'testing-qa','Testing & Quality Assurance','Write comprehensive tests for MVC applications with tools Ginto AI integrates natively.','text','<h2>Testing & Quality Assurance</h2>\n<p><strong>Ginto AI natively supports test generation</strong> for quality code.</p>\n\n<h3>Unit Testing</h3>\n<pre><code>class UserServiceTest extends TestCase {\n  public function testCreateUser() {\n    $service = new UserService($this->mockDb);\n    $user = $service->create([\"email\" => \"test@example.com\"]);\n    $this->assertNotNull($user->id);\n  }\n}</code></pre>\n\n<h3>Integration Testing</h3>\n<pre><code>public function testApiEndpoint() {\n  $response = $this->get(\"/api/users\");\n  $this->assertEquals(200, $response->status);\n}</code></pre>\n\n<h3>Code Coverage</h3>\n<pre><code>phpunit --coverage-html reports/</code></pre>',NULL,NULL,12,45,0,1,NULL,NULL,'2025-12-17 16:47:18','2025-12-17 16:47:18');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (102,1,'data-types','Redis Data Types Deep Dive','Master all Redis data types: Strings, Hashes, Lists, Sets, Sorted Sets.','text','<div class=\"lesson-content\">\n\n<h2>Redis Data Types</h2>\n\n<p>Redis provides five primary data types, each optimized for different use cases. Understanding when to use each type is key to building efficient applications.</p>\n\n<div class=\"info-box bg-teal-50 dark:bg-teal-900/20 border-l-4 border-teal-500 p-4 my-6 rounded-r\">\n<p class=\"font-semibold text-teal-700 dark:text-teal-300\">üìö Official Reference</p>\n<p class=\"text-teal-600 dark:text-teal-400\">For complete data type documentation, see <a href=\"https://redis.io/docs/data-types/\" target=\"_blank\" class=\"underline\">redis.io/docs/data-types</a></p>\n</div>\n\n<h3>1. Strings</h3>\n\n<p>Strings are the most basic Redis data type. They can hold any data: text, serialized JSON, or binary data up to 512MB.</p>\n\n<pre><code class=\"language-bash\"># Basic operations\nSET user:1:name \"John Doe\"\nGET user:1:name              # \"John Doe\"\n\n# With expiration (seconds)\nSETEX session:abc123 3600 \"user_data_here\"\n\n# Atomic increment\nSET counter 10\nINCR counter                 # 11\nINCRBY counter 5             # 16</code></pre>\n\n<p><strong>Common use cases:</strong> Caching, counters, session tokens, simple key-value storage.</p>\n\n<h3>2. Hashes</h3>\n\n<p>Hashes are maps of field-value pairs, ideal for representing objects.</p>\n\n<pre><code class=\"language-bash\"># Store user object\nHSET user:1 name \"John\" email \"john@example.com\" age 30\n\n# Get single field\nHGET user:1 name             # \"John\"\n\n# Get all fields\nHGETALL user:1               # Returns all field-value pairs\n\n# Update single field\nHSET user:1 age 31\n\n# Increment numeric field\nHINCRBY user:1 age 1         # 32</code></pre>\n\n<p><strong>Common use cases:</strong> User profiles, configuration objects, any entity with multiple attributes.</p>\n\n<h3>3. Lists</h3>\n\n<p>Lists are ordered collections of strings, implemented as linked lists. Fast insertion at head or tail (O(1)), but slower random access (O(n)).</p>\n\n<pre><code class=\"language-bash\"># Add to list (left/right)\nLPUSH messages \"message3\"\nLPUSH messages \"message2\"\nLPUSH messages \"message1\"\nRPUSH messages \"message4\"\n\n# Get range (0 = first, -1 = last)\nLRANGE messages 0 -1         # [\"message1\", \"message2\", \"message3\", \"message4\"]\n\n# Pop from list\nLPOP messages                # \"message1\"\nRPOP messages                # \"message4\"\n\n# Trim to keep only recent items\nLTRIM messages 0 99          # Keep first 100 items</code></pre>\n\n<p><strong>Common use cases:</strong> Message queues, recent activity feeds, chat history.</p>\n\n<h3>4. Sets</h3>\n\n<p>Sets are unordered collections of unique strings. Support for set operations like union, intersection, and difference.</p>\n\n<pre><code class=\"language-bash\"># Add members\nSADD online:users \"user1\" \"user2\" \"user3\"\n\n# Check membership\nSISMEMBER online:users \"user1\"   # 1 (true)\nSISMEMBER online:users \"user99\"  # 0 (false)\n\n# Get all members\nSMEMBERS online:users\n\n# Remove member\nSREM online:users \"user1\"\n\n# Set operations\nSADD group:admins \"user1\" \"user2\"\nSADD group:editors \"user2\" \"user3\"\nSINTER group:admins group:editors   # [\"user2\"]</code></pre>\n\n<p><strong>Common use cases:</strong> Tags, unique visitors, online users, relationships.</p>\n\n<h3>5. Sorted Sets</h3>\n\n<p>Sorted sets are like sets but each member has an associated score. Members are ordered by score.</p>\n\n<pre><code class=\"language-bash\"># Add with scores\nZADD leaderboard 100 \"player1\"\nZADD leaderboard 250 \"player2\"\nZADD leaderboard 175 \"player3\"\n\n# Get by rank (highest first)\nZREVRANGE leaderboard 0 2 WITHSCORES\n# Returns: [\"player2\", 250, \"player3\", 175, \"player1\", 100]\n\n# Get rank of member\nZREVRANK leaderboard \"player2\"   # 0 (first place)\n\n# Increment score\nZINCRBY leaderboard 50 \"player1\" # Now 150\n\n# Range by score\nZRANGEBYSCORE leaderboard 100 200</code></pre>\n\n<p><strong>Common use cases:</strong> Leaderboards, priority queues, time-series data (score = timestamp).</p>\n\n<h3>PHP Examples with Predis</h3>\n\n<pre><code class=\"language-php\">&lt;?php\nuse Predis\\Client;\n\n$redis = new Client();\n\n// Strings\n$redis->set(\'cache:user:1\', json_encode([\'name\' => \'John\']));\n$redis->setex(\'session:token\', 3600, \'session_data\');\n\n// Hashes\n$redis->hMSet(\'user:1\', [\n    \'name\' => \'John\',\n    \'email\' => \'john@example.com\'\n]);\n$name = $redis->hGet(\'user:1\', \'name\');\n\n// Lists\n$redis->lPush(\'queue:jobs\', json_encode([\'task\' => \'send_email\']));\n$job = $redis->rPop(\'queue:jobs\');\n\n// Sets\n$redis->sAdd(\'tags:article:1\', \'php\', \'redis\', \'tutorial\');\n$tags = $redis->sMembers(\'tags:article:1\');\n\n// Sorted Sets\n$redis->zAdd(\'leaderboard\', [\'player1\' => 100, \'player2\' => 250]);\n$topPlayers = $redis->zRevRange(\'leaderboard\', 0, 9, \'WITHSCORES\');</code></pre>\n\n<h3>Choosing the Right Data Type</h3>\n\n<div class=\"bg-gray-100 dark:bg-gray-800 rounded-lg p-4 my-6\">\n<table class=\"w-full text-sm\">\n<thead>\n<tr class=\"border-b border-gray-300 dark:border-gray-600\">\n<th class=\"text-left py-2\">Need</th>\n<th class=\"text-left py-2\">Use</th>\n<th class=\"text-left py-2\">Example</th>\n</tr>\n</thead>\n<tbody class=\"text-gray-700 dark:text-gray-300\">\n<tr class=\"border-b border-gray-200 dark:border-gray-700\">\n<td class=\"py-2\">Simple cache</td>\n<td class=\"py-2\">String</td>\n<td class=\"py-2\">Cached API response</td>\n</tr>\n<tr class=\"border-b border-gray-200 dark:border-gray-700\">\n<td class=\"py-2\">Object with fields</td>\n<td class=\"py-2\">Hash</td>\n<td class=\"py-2\">User profile</td>\n</tr>\n<tr class=\"border-b border-gray-200 dark:border-gray-700\">\n<td class=\"py-2\">Queue / recent items</td>\n<td class=\"py-2\">List</td>\n<td class=\"py-2\">Job queue, notifications</td>\n</tr>\n<tr class=\"border-b border-gray-200 dark:border-gray-700\">\n<td class=\"py-2\">Unique collection</td>\n<td class=\"py-2\">Set</td>\n<td class=\"py-2\">Online users, tags</td>\n</tr>\n<tr>\n<td class=\"py-2\">Ranked data</td>\n<td class=\"py-2\">Sorted Set</td>\n<td class=\"py-2\">Leaderboard, priority queue</td>\n</tr>\n</tbody>\n</table>\n</div>\n\n<div class=\"bg-gradient-to-r from-teal-500 to-cyan-500 text-white p-6 rounded-lg mt-8\">\n<h3 class=\"text-xl font-bold mb-2\">üöÄ Next: Caching Patterns</h3>\n<p>Learn how to implement effective caching strategies with Redis.</p>\n</div>\n\n</div>',NULL,NULL,3,35,1,1,NULL,NULL,'2025-12-17 17:23:17','2025-12-17 18:32:13');
INSERT INTO `masterclass_lessons` (`id`, `masterclass_id`, `slug`, `title`, `description`, `content_type`, `content_html`, `content_url`, `embed_file`, `lesson_order`, `duration_minutes`, `is_free_preview`, `is_published`, `resources`, `hands_on_exercises`, `created_at`, `updated_at`) VALUES (103,1,'caching-patterns','Redis Caching Patterns','Implement effective caching strategies: Cache-Aside, Write-Through, and more.','text','<div class=\"lesson-content\">\n\n<h2>Redis Caching Patterns</h2>\n\n<p>Effective caching can dramatically improve application performance. This lesson covers common caching patterns used in production applications.</p>\n\n<div class=\"info-box bg-teal-50 dark:bg-teal-900/20 border-l-4 border-teal-500 p-4 my-6 rounded-r\">\n<p class=\"font-semibold text-teal-700 dark:text-teal-300\">ÔøΩÔøΩ Further Reading</p>\n<p class=\"text-teal-600 dark:text-teal-400\">For more on caching patterns, see <a href=\"https://redis.io/docs/manual/client-side-caching/\" target=\"_blank\" class=\"underline\">Redis Client-side Caching</a></p>\n</div>\n\n<h3>1. Cache-Aside (Lazy Loading)</h3>\n\n<p>The most common pattern. Application checks cache first, loads from database on miss, then populates cache.</p>\n\n<pre><code class=\"language-php\">&lt;?php\nfunction getUser(int $id): ?array\n{\n    global $redis, $db;\n    \n    $cacheKey = \"user:{$id}\";\n    \n    // 1. Try cache first\n    $cached = $redis->get($cacheKey);\n    if ($cached !== null) {\n        return json_decode($cached, true);\n    }\n    \n    // 2. Cache miss - load from database\n    $user = $db->select(\'users\', \'*\', [\'id\' => $id]);\n    if (empty($user)) {\n        return null;\n    }\n    \n    // 3. Populate cache with TTL\n    $redis->setex($cacheKey, 3600, json_encode($user[0]));\n    \n    return $user[0];\n}</code></pre>\n\n<p><strong>Pros:</strong> Only caches data that is actually requested. Cache misses are automatically filled.</p>\n<p><strong>Cons:</strong> First request always hits database. Can have stale data if TTL is too long.</p>\n\n<h3>2. Write-Through</h3>\n\n<p>Data is written to cache and database at the same time.</p>\n\n<pre><code class=\"language-php\">&lt;?php\nfunction updateUser(int $id, array $data): bool\n{\n    global $redis, $db;\n    \n    // 1. Update database\n    $result = $db->update(\'users\', $data, [\'id\' => $id]);\n    \n    if ($result->rowCount() > 0) {\n        // 2. Update cache\n        $user = $db->select(\'users\', \'*\', [\'id\' => $id])[0];\n        $redis->setex(\"user:{$id}\", 3600, json_encode($user));\n        return true;\n    }\n    \n    return false;\n}</code></pre>\n\n<p><strong>Pros:</strong> Cache is always consistent with database.</p>\n<p><strong>Cons:</strong> Write latency increases. May cache data that is never read.</p>\n\n<h3>3. Write-Behind (Write-Back)</h3>\n\n<p>Write to cache immediately, then asynchronously write to database.</p>\n\n<pre><code class=\"language-php\">&lt;?php\nfunction updateUserAsync(int $id, array $data): void\n{\n    global $redis;\n    \n    // 1. Update cache immediately\n    $redis->hMSet(\"user:{$id}\", $data);\n    \n    // 2. Queue database write for later\n    $redis->lPush(\'queue:db_writes\', json_encode([\n        \'table\' => \'users\',\n        \'id\' => $id,\n        \'data\' => $data,\n        \'timestamp\' => time()\n    ]));\n}\n\n// Background worker processes the queue\nfunction processDbWriteQueue(): void\n{\n    global $redis, $db;\n    \n    while ($job = $redis->rPop(\'queue:db_writes\')) {\n        $task = json_decode($job, true);\n        $db->update($task[\'table\'], $task[\'data\'], [\'id\' => $task[\'id\']]);\n    }\n}</code></pre>\n\n<p><strong>Pros:</strong> Very fast writes. Good for high write throughput.</p>\n<p><strong>Cons:</strong> Risk of data loss if Redis crashes. More complex to implement.</p>\n\n<h3>4. Cache Invalidation</h3>\n\n<p>When data changes, you need to invalidate (delete) the cached version.</p>\n\n<pre><code class=\"language-php\">&lt;?php\n// Simple invalidation\nfunction deleteUser(int $id): void\n{\n    global $redis, $db;\n    \n    $db->delete(\'users\', [\'id\' => $id]);\n    $redis->del(\"user:{$id}\");\n}\n\n// Tag-based invalidation for related data\nclass TaggedCache\n{\n    private $redis;\n    \n    public function set(string $key, mixed $value, array $tags, int $ttl = 3600): void\n    {\n        $this->redis->setex($key, $ttl, serialize($value));\n        \n        // Track which keys belong to each tag\n        foreach ($tags as $tag) {\n            $this->redis->sAdd(\"tag:{$tag}\", $key);\n        }\n    }\n    \n    public function invalidateTag(string $tag): int\n    {\n        $keys = $this->redis->sMembers(\"tag:{$tag}\");\n        $count = 0;\n        \n        if (!empty($keys)) {\n            $count = $this->redis->del(...$keys);\n        }\n        \n        $this->redis->del(\"tag:{$tag}\");\n        return $count;\n    }\n}\n\n// Usage\n$cache = new TaggedCache($redis);\n$cache->set(\"user:1:profile\", $profile, [\"user:1\"]);\n$cache->set(\"user:1:settings\", $settings, [\"user:1\"]);\n\n// When user data changes, invalidate all related cache\n$cache->invalidateTag(\"user:1\");</code></pre>\n\n<h3>5. Cache Stampede Prevention</h3>\n\n<p>When a popular cache key expires, many requests may hit the database simultaneously. Use locking to prevent this.</p>\n\n<pre><code class=\"language-php\">&lt;?php\nfunction getWithLock(string $key, callable $loader, int $ttl = 3600): mixed\n{\n    global $redis;\n    \n    // Try cache\n    $value = $redis->get($key);\n    if ($value !== null) {\n        return unserialize($value);\n    }\n    \n    // Try to acquire lock\n    $lockKey = \"lock:{$key}\";\n    $acquired = $redis->set($lockKey, \'1\', \'NX\', \'EX\', 10);\n    \n    if ($acquired) {\n        // We got the lock - load data\n        try {\n            $value = $loader();\n            $redis->setex($key, $ttl, serialize($value));\n            return $value;\n        } finally {\n            $redis->del($lockKey);\n        }\n    }\n    \n    // Another process is loading - wait and retry\n    usleep(100000); // 100ms\n    return getWithLock($key, $loader, $ttl);\n}</code></pre>\n\n<h3>TTL Best Practices</h3>\n\n<div class=\"bg-gray-100 dark:bg-gray-800 rounded-lg p-4 my-6\">\n<table class=\"w-full text-sm\">\n<thead>\n<tr class=\"border-b border-gray-300 dark:border-gray-600\">\n<th class=\"text-left py-2\">Data Type</th>\n<th class=\"text-left py-2\">Suggested TTL</th>\n<th class=\"text-left py-2\">Reason</th>\n</tr>\n</thead>\n<tbody class=\"text-gray-700 dark:text-gray-300\">\n<tr class=\"border-b border-gray-200 dark:border-gray-700\">\n<td class=\"py-2\">Session data</td>\n<td class=\"py-2\">15-60 minutes</td>\n<td class=\"py-2\">Security, user activity</td>\n</tr>\n<tr class=\"border-b border-gray-200 dark:border-gray-700\">\n<td class=\"py-2\">User profile</td>\n<td class=\"py-2\">1-24 hours</td>\n<td class=\"py-2\">Rarely changes</td>\n</tr>\n<tr class=\"border-b border-gray-200 dark:border-gray-700\">\n<td class=\"py-2\">API response</td>\n<td class=\"py-2\">5-60 minutes</td>\n<td class=\"py-2\">Depends on freshness needs</td>\n</tr>\n<tr>\n<td class=\"py-2\">Static content</td>\n<td class=\"py-2\">1-7 days</td>\n<td class=\"py-2\">Rarely changes</td>\n</tr>\n</tbody>\n</table>\n</div>\n\n<div class=\"bg-gradient-to-r from-teal-500 to-cyan-500 text-white p-6 rounded-lg mt-8\">\n<h3 class=\"text-xl font-bold mb-2\">üéì Free Preview Complete</h3>\n<p>You have completed the free Redis lessons! Premium lessons cover Pub/Sub, transactions, Lua scripting, clustering, and more.</p>\n</div>\n\n</div>',NULL,NULL,4,35,1,1,NULL,NULL,'2025-12-17 17:23:17','2025-12-17 18:32:13');
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE IF NOT EXISTS `masterclass_enrollments` (
  `id` int unsigned NOT NULL AUTO_INCREMENT,
  `user_id` int unsigned NOT NULL,
  `masterclass_id` int unsigned NOT NULL,
  `enrolled_at` datetime DEFAULT CURRENT_TIMESTAMP,
  `started_at` datetime DEFAULT NULL,
  `completed_at` datetime DEFAULT NULL,
  `progress_percent` decimal(5,2) DEFAULT '0.00',
  `last_accessed_at` datetime DEFAULT NULL,
  `certificate_issued` tinyint(1) DEFAULT '0',
  `certificate_url` varchar(500) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `unique_user_masterclass` (`user_id`,`masterclass_id`),
  KEY `masterclass_id` (`masterclass_id`),
  CONSTRAINT `masterclass_enrollments_ibfk_1` FOREIGN KEY (`user_id`) REFERENCES `users` (`id`) ON DELETE CASCADE,
  CONSTRAINT `masterclass_enrollments_ibfk_2` FOREIGN KEY (`masterclass_id`) REFERENCES `masterclasses` (`id`) ON DELETE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=209 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;
/*!40101 SET character_set_client = @saved_cs_client */;

